INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("4026", "tofu  turkey  with stuffing", "", "0", "'15-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'occasion', 'main-dish', 'beans', 'oven', 'holiday-event', 'vegetarian', 'dietary', 'christmas', 'thanksgiving', 'soy-tofu', 'equipment', 'number-of-servings'", "3122.1, 363.0, 124.0, 405.0, 424.0, 201.0, 30.0", "25", "'mash tofu or mix well with hands', 'be sure that all of the lumps are out', 'line a 12"" colander with wet cheesecloth over lapping the sides', 'add the mashed tofu to the cloth covered colander , press down and cover with the overlapping sides', 'place the whole thing in a large bowl', 'cover the cheesecloth with a plate that fits inside the colander and place a 5 pound weight on the plate', 'refrigerate and let sit for 2 to 3 hours', 'when time is up , start the stuffing', ""saute' the onions , celery and mushrooms in the 2 tablespoons sesame oil"", 'when soft , add the garlic and all the rest of the stuffing ingredients , except stuffing , mixing well', 'stir and cook for 5 minutes', 'add herb stuffing and mix well', 'remove tofu from refrigerator and take off weight , plate and top of cheesecloth', 'hollow out tofu to within 1 inch of the sides and bottom , placing the tofu in a bowl', 'place the stuffing inside the shell and pack in firmly', 'cover with the remaining tofu and pat down firmly', 'turn stuffed tofu onto a greased baking sheet , flat side down', 'gently press on sides of ""turkey"" to achieve a more oval shape', 'if desired at this point , you may mold ""drumsticks"" out of one pound of tofu , and place on each side of the ""turkey""', 'mix up the basting mixture and baste tofu ""turkey"" with half of it', 'cover the ""turkey"" with foil , and bake at 400 degrees for about 1 hour', 'remove foil , baste with all the remaining mixture except a few tablespoons and return to ov