INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("4117", "bucket lasagna", "this is a method of preparing lasagna which makes 6 dinners which can be frozen and re-heated in a crockpot.", "45", "'60-minutes-or-less', 'time-to-make', 'main-ingredient', 'cuisine', 'preparation', 'pasta', 'european', 'italian', 'lasagna', 'crock-pot-slow-cooker', 'dietary', 'oamc-freezer-make-ahead', 'pasta-rice-and-grains', 'equipment', 'number-of-servings'", "916.9, 81.0, 16.0, 35.0, 198.0, 100.0, 1.0", "17", "'i cook a large package of hamburger , drain', 'place it back in the pan i cooked it in , and add tomato sauce and spices as you like it', 'in another large bowl , mix two large containers of cottage cheese , four eggs , and salt and pepper', 'a third bowl has a large quantity of shredded mozzarella', 'open two packages of lasagna noodles , either no-bake or regular', 'i prefer the regular , they are thicker--he no bake kinda disappear in my lasagna because they are so thin', 'place all the ingredient bowls and pans in a row on the counter', 'then i take 6 ice cream buckets or similar container', 'layer as follows: noodles , sauce , cheese and repeat til done', 'that is it ! you end up with six lasagnas , and only 3 dirty pans', 'freeze', 'once solid , slip from the ice cream bucket , and place in gallon ziploc bags', ', now they go back in the freezer , taking up much less space', 'i use the ice cream bucket because it makes it the exact correct size and shape for my 5 quart crockpot', 'you may find another container that works better for you', 'when its lasagna eating day , just pop it in the crockpot on low , and let her go all day', 'come home , dinner is ready !'", "'hamburger', 'tomato sauce', 'spices', 'cottage cheese', 'eggs', 'salt', 'pepper', 'mozzarella cheese', 'lasagna noodles', 'ice cream buckets'", "10", "https://www.food.com/recipe/4117");
