INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("4151", "burst o  lemon muffins", "only try these if you love lemon as they are pretty intense. i used the grater on the zest of one lemon and it was no where near 1-2 tbs  but i think it was plenty. we liked them but my son preferred his without the glaze.  i'd like to try the glaze with confectioners sugar and try the muffins with vanilla yogurt some time.", "43", "'60-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'breads', 'fruit', 'oven', 'muffins', 'dietary', 'quick-breads', 'equipment', 'number-of-servings'", "236.5, 13.0, 86.0, 9.0, 7.0, 30.0, 12.0", "14", "'in a large bowl , combine the flour , sugar , baking powder , baking soda and salt', 'in another bowl , beat the yogurt , egg , butter , lemon peel and lemon juice until smooth', 'stir into dry ingredients just until moistened', 'fold in the coconut', 'fill greased muffin cups two-thirds full', 'bake at 400f for 18-22 minutes or until golden brown and muffins test done', 'cool for 5 minutes before removing from pan to wire rack', 'in a saucepan , combine the lemon juice and sugar', 'cook and stir until sugar is dissolved', 'stir in coconut', 'using a toothpick , poke 6-8 holes each muffin', 'spoon the coconut mixture over muffins', 'serve warm or cool to room temperature', ""we have found we prefer them cooled as the lemon flavor isn't as overwhelming once they reach room temperature""", "'all-purpose flour', 'sugar', 'baking powder', 'baking soda', 'salt', 'lemon yogurt', 'egg', 'butter', 'lemon zest', 'lemon juice', 'coconut', 'flaked coconut'", "12", "https://www.food.com/recipe/4151");
