INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("4207", "microwave gravy", "these recipes are adapted from barbara kafka's microwave gourmet. they are based on an oven of 650 to 700 watt oven, which is low by today's standards. if your microwave is lower or higher power, you should adjust the times.  veloute      ", "0", "'15-minutes-or-less', 'time-to-make', 'course', 'preparation', 'low-protein', 'condiments-etc', 'microwave', 'dietary', 'low-carb', 'low-in-something', 'equipment', 'number-of-servings'", "559.2, 86.0, 2.0, 21.0, 11.0, 174.0, 3.0", "21", "'heat butter in an 8 cup glass measuring cup at 100% , uncovered , for 2 min', 'remove from oven', 'thoroughly whisk in flour', 'cook , uncovered at 100% for 2 min more', 'remove from oven', 'whisk in cream and broth', 'cook , uncovered at 100% for 2 min', 'stir and cook for 3 min more', 'remove from oven , add salt and pepper and whisk until smooth', 'add lemon juice to taste', 'variation with mushrooms increase butter to 4 tbsp', 'add flour and cook as for veloute', 'add 1 / 4 lb fresh mushrooms , quartered and sliced', 'you may also substitute dried mushrooms for a portion of the fresh mushrooms', 'add lemon juice and a dash of cayenne pepper', 'cook , uncovered at 100% for 3 min , finish as for veloute above', 'another microwave sauce to use as gravy is marchand du vin sauce makes 1 1 / 2 cups 1 / 4 cup chopped shallots 1 clove garlic , smashed , peeled , and minced 2 cups red wine 1 cup chicken , duck , or veal broth 1 tbsp chicken , duck or veal glaze 2 tsp cornstarch dissolved in 3 tsp cold water fresh ground pepper put shallots , garlic , wine and 1 / 2 cup of broth in a 8 cup glass measure', 'cook , uncovered , at 100% for 20 minutes , until reduced by one half', 'remove from oven and deglaze roast cooking pan with this mixture , then cook on top of the stove until slightly thickened , and season to taste', 'if not deglaz