INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("4216", "english spiced beef", "this was one of the great victorian christmas season dishes; the perfect centre piece for a cold supper buffet. a neat round joint of silverside or topside is the best for this kind of dish.", "0", "'15-minutes-or-less', 'time-to-make', 'main-ingredient', 'cuisine', 'preparation', 'occasion', 'beef', 'oven', 'european', 'holiday-event', 'english', 'dietary', 'meat', 'equipment', 'number-of-servings'", "53.4, 0.0, 54.0, 307.0, 0.0, 0.0, 4.0", "16", "'saltpeter is a necessary preservative when curing meat and also adds a pleasing red color otherwise the beef will turn out a murky grey', 'rub the meat all over with the sugar and put it in a pot with a lid for 2 days', 'leave it in a cool place , turning and rubbing the meat occasionally with the sticky liquor that develops', 'crush the peppercorns , juniper and allspice with a pestle and mortar and mix with the salt and saltpeter', 'rub this mixture well into the meat and leave it in the pot for a further 9 days', 'turn the meat and rub the pickling mixture into the flesh daily', 'when the pickling is complete , remove the beef and rub off any excess spices adhering to the surface', 'do not rinse it', 'wrap the meat in cooking foil [the victorians used suet] and put into a large ovenproof pot', 'add about 10 fl oz water , cover the pot with a double layer of foil and fit the lid on tightly', 'preheat the oven to 275 deg f', 'bake the meat slowly allowing 45 min per lb', 'when the meat is cooked , remove the pot and set aside until it is quite cold', 'unwrap the meat , drain off any excess liquid and place on a board', 'cover with foil , put a weight on it and leave for at least 24 hr', 'carve the meat into thin slices and serve with anything from good mustard to pickled kumquats'", "'joint of beef', 'light brown sugar', 'black peppercorns', 'juniper berries