INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("4493", "homemade phyllo", "", "0", "'15-minutes-or-less', 'time-to-make', 'course', 'cuisine', 'preparation', 'occasion', 'healthy', '5-ingredients-or-less', 'greek', 'easy', 'european', 'dietary', 'low-cholesterol', 'low-saturated-fat', 'low-in-something', 'number-of-servings'", "1453.5, 46.0, 3.0, 97.0, 68.0, 21.0, 84.0", "19", "'sift flour and salt into a bowl', 'gradually add water , stirring to make a stiff dough', 'turn onto a pastry board', 'place the oil in a bowl and spread a little of it on the palms of your hands', 'knead the dough with a folding and turning motion , adding more oil to your hands when dough begins to stick', 'continue until you have a smooth , elastic ball of dough and the oil is nearly all used', 'then roll the ball of dough in the remaining oil to cover all sides , place a clean cloth over the bowl , and allow the dough to rest for two hours or more in a warm place away from drafts', 'a barely warm oven is satisfactory', 'separate 1 / 4 of the dough and roll to 1 / 4 inch thickness on a pastry board rubbed with cornstarch', 'cover with a clean cloth and let it rest for 10 minutes', 'cover a table with a smooth cloth and carefully lift dough onto it', 'put your hands under the dough , palms down and gently stretch and pull the dough with the backs of your hands , working your way around the table , until the dough is as thin as tissue paper', 'do not worry if it hangs down around the edges of the table , or if some holes appear , especially around the edges', 'cut off the thicker edge and save the scraps', 'the phyllo is now ready to be cut into pieces with scissors if you wish to use it moist', 'if you prefer dry phyllo , allow it to sand until dry , about 10 minutes , then cut into desired sizes', 'the scraps of dough can be put into a moist bowl and kneaded and rolled again', 'when using phyllo dough , 