INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("4582", "roasted loin of wild boar with apple charlotte", "boar is a greatly ignored meat in the chef's repertoire. while admittedly difficult to find, it can be special ordered, and it is a sublime gastronomic treat. this homespun preparation by joe mannke, chef a rotisserie for beef and bird in houston, marries the boar with a tasty apple charlotte that is the perfect companion.", "0", "'15-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'occasion', 'main-dish', 'fruit', 'pork', 'oven', 'roast', 'dinner-party', 'dietary', 'wild-game', 'apples', 'meat', 'equipment'", "337.4, 28.0, 49.0, 59.0, 6.0, 53.0, 11.0", "19", "'preheat oven to 450f', 'rub boar on all sides with salt , pepper , and thyme', 'place boar in a roasting pan and add water to pan', 'roast in preheated oven for 40 minutes', 'remove roasting pan from oven', 'prepare charlotte by adding apples , zest , ginger , cinnamon , cloves , brown sugar , wine , and calvados around the roast', 'return pan to oven and bake for 15 to 20 minutes , or until apples are cooked through', 'mix together applesauce , salt , and egg yolks and pour over apples', 'return to oven for 5 minutes longer', 'mix the butter with the bread cubes', 'sprinkle over the apple mixture and bake for 10 minutes longer , or until golden brown , or when meat thermometer registers 160f', 'remove roasting pan from oven , remove boar , and let rest for a few minutes', 'slice boar on the bias', 'using a spatula , transfer charlotte to a large serving platter', 'place sliced boar atop charlotte', 'garnish with thyme sprigs and serve', 'recommended wines: a full bodied chardonnay will mar ry nicely with the apple flavor in the charlotte', 'a zinfandel will emphasize the meaty flavors of the boar', 'either wine is a treat with this dish'", "'loin of wild boar', 'kosher salt', 'black pepper'