INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("4861", "cherry pie", "i got this recipe from my sister-in-law when i got married and my husband's aunt had a cherry tree. it is our favorite! prep time does not include the time it takes to pit the cherries or make the pie crust. ;) i use the pie crust recipe in the fanny farmer baking book. recipe #13619 is the recipe for 2 8-inch crust. (i've found i have to refrigerate it for the pie filling to firm up and not be ""soupy"" when it has cooled. once it has completely chilled, you can store it at room temperature and it doesn't lose its firmness. or, store it in the fridge and warm it in the microwave a little bit before serving it with ice cream or whipped cream if desired.)", "70", "'time-to-make', 'course', 'main-ingredient', 'cuisine', 'preparation', 'occasion', 'north-american', 'low-protein', 'healthy', '5-ingredients-or-less', 'pies-and-tarts', 'desserts', 'fruit', 'american', 'oven', 'easy', 'low-fat', 'spring', 'pies', 'dietary', 'low-sodium', 'low-cholesterol', 'seasonal', 'low-saturated-fat', 'low-calorie', 'low-in-something', 'pitted-fruit', 'cherries', 'equipment', 'number-of-servings', '4-hours-or-less'", "159.9, 0.0, 139.0, 0.0, 2.0, 0.0, 13.0", "8", "'mix ingredients for filling', 'place in pastry-lined pie pan', 'put top crust on', 'cut vent slits in top crust , and flute edges', 'brush with milk and sprinkle with sugar', 'bake at 425f for 25 minutes , reduce heat to 350f and bake for 25-30 minutes more', 'add the cherries and toss to coat', 'this coats them more even than doing it in a bowl and the filling is firmer'", "'cherries', 'sugar', 'flour', 'cinnamon', 'pastry for a double-crust 9-inch pie'", "5", "https://www.food.com/recipe/4861");
