INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("4876", "millet loaf", "this recipe serves a crowd and can be halved.", "90", "'time-to-make', 'course', 'main-ingredient', 'cuisine', 'preparation', 'north-american', 'low-protein', 'healthy', 'main-dish', 'vegetables', 'american', 'oven', 'low-fat', 'vegetarian', 'dietary', 'low-sodium', 'low-cholesterol', 'low-saturated-fat', 'low-calorie', 'healthy-2', 'low-in-something', 'equipment', 'number-of-servings', '4-hours-or-less'", "149.8, 4.0, 7.0, 5.0, 8.0, 2.0, 9.0", "11", "'rinse millet and put it in a medium saucepan with the water and 1 / 2 tsp salt', 'cook , lid ajar slightly , over medium heat for about 20 minutes , or until soft', 'the millet should absorb all the water', 'saute the carrots , celery , onions and garlic in sesame oil until onions are translucent', 'add the seasonings', 'mix the cooked millet and the veggies together , along with the sunflower seeds , if you wish', 'add 3 tbsp flour to the millet mixture , blending well', 'lightly oil 2 loaf pans', 'press the millet mixture into the pan , and bake at 400 f for one hour', 'allow the loaf to cool for about 10 minutes before turning it out onto a platter and slicing', 'serve with mashed potatoes , green beans and lots of brown gravy'", "'millet', 'water', 'salt', 'carrots', 'celery', 'onion', 'garlic', 'sesame oil', 'dill weed', 'dried thyme', 'roasted sunflower seeds', 'whole wheat flour'", "12", "https://www.food.com/recipe/4876");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("4877", "banana oatmeal cake with caramel fudge frosting", "", "0", "'15-minutes-or-less', 'time-to-make', 'course', 'preparation', 'occasion', 'for-large-groups', 'desserts', 'oven', 'cakes', 'dietary', 'equipment', 'number-of-servings'", "135.1, 7.0, 39.0, 7.0, 4.0, 13.0, 7.0", "18", "'in medium bowl mix together flour , rolled oats , baking powder , baking soda , salt and nutmeg', 'in large bowl , with electric mixer , cream together butter and sugar until light and fluffy', 'add eggs and vanilla , beating well', 'add dry ingredients alternately with bananas and buttermilk , mixing lightly after each addition', 'pour batter into greased 13 x 9 inch cake pan', 'bake at 350 f for 35 - 40 minutes or until toothpick inserted in centre comes out clean', 'cool completely on cake rack', 'ice with caramel fudge frosting', 'frosting: 1 / 3 cup butter 1 cup lightly packed brown sugar 1 / 4 cup milk , 2% 2 cups sifted icing suga r melt butter in saucepan', 'stir in brown sugar', 'heat to boiling , stirring constantly', 'boil and stir over low heat 2 minutes', 'stir in milk', 'heat to boiling', 'remove from heat', 'gradually stir in icing sugar', 'spread on cooled cake', 'yield: 24 servings garnish plate with a sliced strawberry and banana slices that have been dipped in orange or lemon juice'", "'all-purpose flour', 'rolled oats', 'baking powder', 'baking soda', 'salt', 'nutmeg', 'butter', 'granulated sugar', 'eggs', 'vanilla', 'bananas', 'buttermilk'", "12", "https://www.food.com/recipe/4877");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("4880", "albanian byrek", "the directions to this are vague, but maybe you can use it.", "0", "'15-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'cuisine', 'preparation', 'occasion', 'main-dish', 'vegetables', 'oven', 'european', 'dietary', 'equipment', 'number-of-servings'", "354.4, 42.0, 25.0, 59.0, 37.0, 37.0, 2.0", "14", "'prepare the dough with flour , 1 and a half cups of cold water , 1 spoon of oil , 1 spoon of vinegar and half a teaspoon of salt , so that the dough may obtain average solidity', 'press and mix the dough well , then divide in two equally sized pieces', 'then divide the pieces into 10-12 ball shaped pieces', 'spread the balls in well-shaped circles of the size of a plate', 'place the circle-shaped dough on top of one another so as to obtain two large pies', 'cover each pie with melted butter and leave for about half an hour to 1 hour', 'during this time wash a bowl of spinach , dock or sorrel , cut thin , add salt and leave in salt for about 15-20 minutes , then squash', 'mix the spinach , dock or sorrel with half to 1 cup yogurt and 2-3 eggs', 'you can replace the yogurt with crumbled feta cheese', 'cover the baking pan with melted butter', 'spread one of the dough pies to cover the entire pan and gently pull up the ends , then sprinkle melted butter on it and spread the spinach on it uniformly', 'spread the second dough pie on top of the spinach', 'cut the byrek in square slices , sprinkle with melted butter and place in a hot oven until cooked', 'byrek is served hot accompanied with yogurt'", "'flour', 'water', 'oil', 'vinegar', 'salt', 'butter', 'spinach', 'yogurt', 'eggs'", "9", "https://www.food.com/recipe/4880");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("4882", "hungarian chicken paprika", "this is a  recipe for chicken paprika, a recipe my father brought over from hungary. it is simply marvelous. (it bears no relationship to cacciatore.)", "0", "'15-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'cuisine', 'preparation', 'occasion', 'main-dish', 'poultry', 'hungarian', 'european', 'dinner-party', 'chicken', 'dietary', 'meat', 'whole-chicken'", "848.1, 89.0, 19.0, 44.0, 108.0, 114.0, 8.0", "47", """sauce procedure: cut whole chicken into it's constituent parts and remove skin"", 'remove skin from thighs', 'this step is critical', 'cut all chicken pieces in half', 'this exposes the crucial bone marrow', 'the more marrow you expose , the better the sauce', 'i chop the wings into 4 or five pieces', 'do not add giblets and neck', 'in large pot , combine chicken , salt , paprika , chicken base , and enough water to just cover the chicken pieces', 'cook , partially covered , at such temperature that a simmer to very gentle boil is achieved', 'you want just a bit of bubbling', 'do so until the chicken is thoroughly cooked and easily removed from the bone--about 1', '5 hrs', 'strain mixture to separate liquid and chicken', 'place chicken into a warm serving dish', 'return liquid to pot , and vigorously mix in sour cream and milk', 'i use a whisk for this', 'carefully bring the sauce to a boil and remove immediately from heat', 'failure to remove promptly will result in very messy boiling over !', ""set burner to it's lowest setting and return pot to the burner to keep sauce hot"", 'the sauce is basically finished at this point', 'you may wish to fine tune by adding more salt , sour cream , paprika , and / or chicken base', 'i sometimes add a 1 / 2 cup or so of heavy cream for added richness', 'you may also want to skim most of the liquid fat from the sauce surface', 'noodles procedure: combine flour , salt , and eggs in a hemispherical bowl large enough to accommodate mixing', 'add about 1 / 2 cup water and proceed to blend ingredients with a fork until well blended', 'the consistency your striving for is such that the dough is clearly wet , adheres to the bowl , and is loose enough to slowly spread out when a dollop is applied to the cutting board', 'add enough water to achieve this', ""i've never measured how much water i use , but think it's about 1 / 2 cup plus some"", 'actually , the consistency is not critical', ""i'm guessing you've made noodles like this , and that additional , excruciating explanation is not necessary"", 'set a dutch oven 2 / 3 full of water to boil', 'at this point , obviously the dough goes into the water', 'i place a good size dollop on a small cutting board and use a gently curved , sharp knife to cut perhaps 1 / 2"" size blobs which are then vigorously swept off the board into the boiling water', 'they are done when they float to the surface', 'a perforated spoon is used to remove the noodles from the water which are placed in a warm serving bowl', 'i cut perhaps 3-4 dozen noodles , let them cook , remove , and proceed to add the next 3-4 dozen until all of the dough is consumed', 'if it looks like boiling over is about to occur , add some cold water', 'finally: at this point you should have a pot of wonderful sauce , a bowl full of chicken pieces , and a bowl of noodles', 'for serving , place a good serving of noodles and half as much chicken into a flat bowl', 'apply enough sauce to almost cover noodles and chicken', 'you may want to salt to taste', 'my wife likes pepper on it', 'i do not', ""in any case , viola ! that's it"", 'i really want you to try this , as everyone on both sides of our family loves it', 'my instructions are laborious because attention paid to various details will produce a superb dish , while an average effort with average ingredients produce a result that is only very good'", "'chicken', 'sour cream', 'chicken thighs', 'whole milk', 'paprika', 'salt', 'water', 'chicken base', 'unbleached white flour', 'eggs'", "10", "https://www.food.com/recipe/4882");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("4891", "bon appetit s mulligatawny soup   chicken", "this classic anglo-indian soup, the name of which means ""pepper water,"" should be richly endowed with meat and piquantly spiced. taste the soup as it cooks, adding lemon juice, cayenne pepper and curry powder as necessary.", "40", "'60-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'soups-stews', 'poultry', 'chicken', 'dietary', 'low-sodium', 'low-calorie', 'low-carb', 'low-in-something', 'meat', 'whole-chicken', 'number-of-servings'", "462.1, 43.0, 38.0, 17.0, 49.0, 55.0, 9.0", "20", "'combine garlic and spices', 'melt butter in large skillet over medium-high heat', 'add chicken and saute until lightly browned on all sides', 'add giblets and saute until cooked through', 'transfer chicken and giblets to stockpot', 'drain all but 1 tablespoon fat from skillet', 'add celery , onion , carrot , leek and spice mixture and blend well', 'add a small ladle of stock and cook over low heat , stirring constantly , until vegetables are tender', 'add to chicken', 'stir in remaining stock and season with salt and pepper', 'cover and simmer 30 minutes', 'remove chicken with slotted spoon and set aside', 'add rice to soup and continue cooking 15 minutes', 'when chicken is cool enough to handle , cut meat into bite-size pieces , discarding skin and bones', 'return chicken to soup and blend in apples and yogurt', 'simmer 10 minutes', 'degrease soup if necessary', 'stir in lemon juice , then blend in cream', 'taste and adjust seasoning', 'pour into heated tureen and sprinkle with parsley and almonds'", "'garlic clove', 'cumin seed', 'whole cloves', 'curry powder', 'ginger', 'cayenne pepper', 'unsalted butter', 'roasting chickens', 'chicken giblets', 'celery', 'onions', 'carrots', 'leek', 'chicken stock', 'salt & pepper', 'long grain rice', 'apples', 'plain yogurt', 'fresh lemon juice', 'whipping cream', 'fresh parsley', 'sliced almonds'", "22", "https://www.food.com/recipe/4891");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("4892", "jenny gentile s pizza dough", "this is a thin, crisp dough.  its awesome from my friends italian grandma, who is no longer with us.  her dough lives on.", "150", "'time-to-make', 'course', 'cuisine', 'preparation', 'healthy', 'flat-shapes', 'breads', 'main-dish', 'european', 'low-fat', 'pizza', 'italian', 'dietary', 'low-cholesterol', 'low-saturated-fat', 'healthy-2', 'low-in-something', '4-hours-or-less'", "261.9, 4.0, 12.0, 16.0, 13.0, 2.0, 17.0", "5", "'in a cup dissolve 1 pack of yeast in 4 tablespoons of warm water', 'allow to proof for 10 minutes', 'in another cup , mix together 12 tablespoons of cold water , 1 tablespoon of olive oil , 1 teaspoon of salt and 1 tablespoon of honey', 'add the wet ingredients to 3 cups of flour and mix allow the dough to rise', 'personally , i make the dough the night before i need it , put it in the refrigerator overnight , and let it rise the next day but as long as you let it rise at least 2 hours , it should be ok'", "'yeast', 'water', 'olive oil', 'salt', 'honey', 'flour'", "6", "https://www.food.com/recipe/4892");
