INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("4902", "corn side dish", "this is not only fairly easy, but if you have all of stuff ready can be thrown together in the microwave and it's done in 12-15 minutes.  i got it when i bought my panasonic microwave way back in the 70's.  it is very good & very different.", "25", "'30-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'cuisine', 'preparation', 'occasion', 'north-american', 'low-protein', 'healthy', 'main-dish', 'condiments-etc', 'vegetables', 'canadian', 'oven', 'easy', 'microwave', 'no-cook', 'roast', 'holiday-event', 'low-fat', 'broil', 'food-processor-blender', 'dietary', 'one-dish-meal', 'low-cholesterol', 'low-saturated-fat', 'low-calorie', 'cooking-mixes', 'healthy-2', 'ontario', 'low-in-something', 'pasta-rice-and-grains', 'corn', 'to-go', 'equipment', 'small-appliance', 'number-of-servings', 'technique'", "147.5, 4.0, 45.0, 30.0, 8.0, 2.0, 10.0", "7", "'in 2-1 / 2 to 3 qt casserole dish , toss celery and green pepper in oil', 'microwave on high for 2 minutes', 'add all rest of ingredients', 'mix thoroughly , cover and cook on medium high until done for 9-10 minutes', ""that's a guess , you'll have to check to make sure everything is done and hot"", 'this is also good cold', 'i use it when we have block parties or large picnics'", "'celery', 'green pepper', 'olive oil', 'frozen corn', 'tomato sauce', 'mushroom stems and pieces', 'tomatoes', 'brown sugar', 'onion powder', 'garlic powder', 'salt', 'black pepper', 'cayenne pepper'", "13", "https://www.food.com/recipe/4902");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("4903", "day after the holiday turkey soup", "", "32", "'60-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'occasion', 'soups-stews', 'poultry', 'holiday-event', 'turkey', 'dietary', 'christmas', 'thanksgiving', 'meat'", "68.6, 3.0, 14.0, 8.0, 6.0, 2.0, 3.0", "8", "'in a heavy saucepan , heat olive oil over medium-high heat', 'reduce heat and add finely diced onions , carrots , and celery', 'cook until soft and golden brown , about 5 to 6 minutes', 'add corn , turkey , salt and pepper and the chicken stock then bring to a boil', 'let the soup simmer uncovered for 10 to 15 minutes', 'season with salt and pepper', 'before serving squeeze in the juice of a lemon', 'leftover rice may be substituted for the corn'", "'olive oil', 'onion', 'carrots', 'celery', 'chicken stock', 'corn', 'turkey', 'salt', 'pepper', 'lemon'", "10", "https://www.food.com/recipe/4903");
