INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("5124", "raspberry mousse cake", "a real treat. but-when you assemble this i would advise you to put the sponge base in a springform tin or something that will keep the sides straight. my girlfriend and i made it and the mousse started to ooze down the sides, the picture she had showed it looking really straight sided. but it was soooooooo yummy.", "75", "'time-to-make', 'course', 'main-ingredient', 'preparation', 'occasion', 'desserts', 'fruit', 'oven', 'cakes', 'dietary', 'berries', 'raspberries', 'equipment', 'number-of-servings', '4-hours-or-less'", "2960.5, 322.0, 640.0, 45.0, 100.0, 604.0, 79.0", "24", "'preheat oven to 180c', 'brush a 20cm round spring form tin with melted butter', 'sift flours and cornflour 3 times onto greaseproof paper', 'beat eggs until thick and pale', 'add sugar gradually , beating constantly until dissolved and mixture is pale yellow and glossy', 'transfer mixture to a large mixing bowl', 'using a metal spoon , fold in dry ingredients quickly and lightly', 'spread mixture into prepared tin', 'bake 15 minutes or until sponge is lightly golden and shrinks from the side of tin', 'leave sponge in tin for 3 minutes before turning onto wire rack to cool', 'to make mousse: process 315g of the raspberries to a smooth texture in blender or processor , pass through a fine sieve', 'reserve half of the puree for serving', 'beat egg and sugar together until creamy', 'add cream cheese , beat until smooth', 'in a separate bowl beat cream until soft peaks form', 'combine gelatin with water in a small bowl , stand bowl in hot water , stir until gelatin dissolves', 'using a metal spoon , fold gelatin , half of the puree and reserved whole raspberries into cream cheese mixture , then fold in cream', 'cover with plastic wrap and put in the refrigerator 10 minutes or until the mixture has thickened , stirring occasionally', 'to assemble: cut cake in half horizontally', 'place first cake layer on a board', 'spread cake evenly with mousse', 'top with remaining cake layer , refrigerate until mousse is set', 'for serving , sift icing sugar over top of the cake', 'cut cake into wedges , place on individual serving plates and spoon reserved puree around cake'", "'plain flour', 'self raising flour', 'cornflour', 'eggs', 'caster sugar', 'fresh raspberries', 'cream cheese', 'cream', 'unflavored gelatin', 'water', 'icing sugar'", "11", "https://www.food.com/recipe/5124");
