INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("5140", "hungarian angel wing fry cookies  csoroge", "these cookies are those wonderfully crispy, light as a feather fried dough cookies always made for weddings. they look so beautiful when piled high in a pyramid on a cut crystal platter. these pastries would also be made for a sunday dinner and served with coffee after a meal of beef gulyas or chicken paprikas. it always left traces of powdered sugar on your upper lip, and sometimes on the tip of your nose.", "4", "'15-minutes-or-less', 'time-to-make', 'course', 'cuisine', 'preparation', 'healthy', 'rolled-cookies', 'desserts', 'hungarian', 'european', 'cookies-and-brownies', 'deep-fry', 'dietary', 'low-sodium', 'low-in-something', 'technique'", "79.4, 2.0, 4.0, 0.0, 4.0, 2.0, 4.0", "15", "'place in the center of a bread board one cup of flour', 'make a dent or well in the center', 'add the whole yolks , sour cream , sugar , rum and salt', 'with a fork mix until the liquids are well combined', 'gradually work into the flour', 'the dough should be of the consistency of a noodle dough', 'knead for a few minutes to make the dough smooth', 'split the dough into 2 portions', 'on a lightly floured board roll out each section until paper thin', 'if you have a pie crimping wheel , use it to cut the dough into squares of 3 1 / 2-inches or a rectangle of 3 1 / 2 x 2 1 / 2-inches', 'they will have beautiful serrated edges', 'with a paring knife make three or four gashes about 2 to 2 1 / 2-inches evenly spaced', 'fry in a deep fat until golden brown', 'drain on paper towel', 'sprinkle with sifted confectioners sugar'", "'egg yolks', 'sour cream', 'granulated sugar', 'rum', 'salt', 'all-purpose flour'", "6", "https://www.food.com/recipe/5140");
