INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("5150", "down east haddock chowder", "chowders are simple soups - usually just fish, potato, onion and milk. they are quick to make and among the best soups in canada. this haddock chowder is a recreation of the popular chowder served at the evangeline snack bar in grand pri, n.s. when asked the secret of the chowder's success, the waitress  modestly answered, ", "0", "'15-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'occasion', 'healthy', 'lunch', 'soups-stews', 'eggs-dairy', 'potatoes', 'seafood', 'vegetables', 'low-fat', 'chowders', 'dietary', 'low-cholesterol', 'low-saturated-fat', 'low-calorie', 'low-carb', 'low-in-something'", "187.2, 8.0, 8.0, 26.0, 31.0, 17.0, 6.0", "14", "'place fish , water and salt in large saucepan', 'bring to boil , reduce heat and simmer gently , uncovered , for 8 to 10 minutes', 'fish is done when flesh is opaque', 'remove immediately and when cool enough to handle , break into bite-size pieces', 'reserve until rest of soup is ready', 'skim any foam off fish stock', 'add potatoes , onion , celery and pepper', 'cover and bring to boil', 'reduce heat and simmer until tender', 'return fish to pan', 'pour in milk and heat through without boiling', 'taste and adjust seasoning', 'swirl in butter', 'transfer to heated tureen or soup bowls and serve immediately'", "'haddock fillets', 'water', 'salt', 'potatoes', 'onion', 'celery', 'pepper', 'milk', 'butter'", "9", "https://www.food.com/recipe/5150");
