INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("5174", "amish dip", "this recipe comes from marsha adams, who hosted a program on american public broadcasting stations called quilt country. the first time i tried it, i knew it was something that i had to share with you. i use those spanish green olives that are stuffed with pimentos, because it adds some nice color.since the recipe calls for diced, i save a little money by using the ones that come sliced in the jar because they are made from olives that are not perfect enough to be sold whole", "15", "'15-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'cuisine', 'preparation', 'occasion', 'north-american', 'appetizers', 'seafood', 'american', 'easy', 'heirloom-historical', 'holiday-event', 'dips', 'fish', 'dietary', 'amish-mennonite', 'tuna', 'northeastern-united-states', 'saltwater-fish', 'number-of-servings', '3-steps-or-less'", "502.9, 67.0, 12.0, 18.0, 44.0, 84.0, 2.0", "2", "'mix all the ingredients , adding more or less mayonnaise depending on your desire to make a chip dip , or a sandwich spread --', 'it was often served on dark heavy bread as a sandwich at quilting bees'", "'cream cheese', 'mayonnaise', 'tuna', 'olive', 'walnuts', 'lemon juice', 'black pepper'", "7", "https://www.food.com/recipe/5174");
