INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("5178", "anise almond biscotti", "", "0", "'15-minutes-or-less', 'time-to-make', 'course', 'preparation', 'desserts', 'oven', 'cookies-and-brownies', 'dietary', 'biscotti', 'equipment', 'number-of-servings'", "118.1, 10.0, 9.0, 3.0, 5.0, 13.0, 4.0", "18", "'to toast almonds , preheat oven to 350=b0f', 'spread almonds in a single layer on a baking sheet and toast , turning occasionally , for 10 minutes', 'set aside to cool , then roughly chop', 'sift together flour , baking powder and salt in a small mixing bowl', 'set aside', 'cream butter and sugar with an electric mixer in a large mixing bowl until fluffy', 'then beat in eggs and vanilla', 'gradually add flour mixture , mix until combined , then stir in chopped almonds and anise seeds', 'dough will be stiff', 'transfer dough to a lightly floured work surface , then , using your hands or a rolling pin , shape into one long roll , about 3 inches by 13 inches', 'line baking sheet with parchment paper or aluminum foil', 'transfer dough roll to baking sheet , flatten slightly , then bake until golden brown , about 35 minutes', 'remove from oven--do not turn oven off--and set aside until cool enough to handle , about 15 minutes', 'using a serrated knife , cut roll , on the diagonal , into 1 / 2 inch thick slices', 'place slices flat on baking sheet and bake , turning once , until golden brown , about 5 minutes per side', 'do not over bake: biscotti will feel soft in the middle but will become crisp and hard as they cool', 'allow to cool completely on pan', 'biscotti will keep in a sealed container for up to 4 weeks'", "'almonds', 'flour', 'baking powder', 'salt', 'butter', 'sugar', 'eggs', 'vanilla extract', 'anise seed'", "9", "https://www.food.com/recipe/5178");
