INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("5343", "orange date loaves", "wonderful rich date flavour.", "70", "'time-to-make', 'course', 'main-ingredient', 'cuisine', 'preparation', 'occasion', 'north-american', 'for-large-groups', 'healthy', 'breads', 'fruit', 'american', 'oven', 'dietary', 'quick-breads', 'equipment', 'number-of-servings', '4-hours-or-less'", "284.5, 12.0, 94.0, 6.0, 10.0, 5.0, 16.0", "12", "'in a large mixing bowl stir together flour , sugar , baking powder , orange peel , baking soda , and salt', 'in another mixing bowl stir together eggs , 1 / 2 cup of the thawed orange juice concentrate , the oil , and 1-1 / 2 cups water', 'add to flour mixture', 'stir by hand just until combined', 'fold in dates and nuts', 'divide mixture evenly between two greased 8""x4""x2"" loaf pans', 'bake in a 350 degree oven for 50 to 55 minutes or until a toothpick inserted near the centers comes out clean', 'cool in pans for 10 minutes', 'remove from pans', 'generously brush tops and sides of loaves with remaining orange juice concentrate', 'cool thoroughly on wire racks', 'wrap and store overnight before slicing'", "'all-purpose flour', 'sugar', 'baking powder', 'orange rind', 'baking soda', 'salt', 'eggs', 'frozen orange juice concentrate', 'cooking oil', 'dates', 'pecans'", "11", "https://www.food.com/recipe/5343");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("5344", "marinated flank steak with citrus salsa", "", "0", "'15-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'cuisine', 'preparation', 'occasion', 'north-american', 'sauces', 'main-dish', 'condiments-etc', 'mexican', 'barbecue', 'salsas', 'marinades-and-rubs', 'dietary', 'low-sodium', 'gluten-free', 'free-of-something', 'low-in-something', 'equipment', 'grilling'", "687.3, 37.0, 75.0, 24.0, 111.0, 45.0, 19.0", "25", "'for marinade , in a small bowl combine thawed orange or grapefruit juice concentrate , finely chopped jalapeno pepper , black pepper and paprika', 'reserve 2 tablespoons of the mixture of salsa', 'add the water to remaining mixture', 'score steak by making shallow cuts at 1-inch intervals diagonally across steak in a diamond pattern', 'repeat on second side', 'place in a plastic bag set in a shallow dish', 'pour marinade over steak', 'close bag', 'marinate in the refrigerator 2 to 24 hours', 'turn bag occasionally', 'for salsa , in a nonmetallic bowl stir together the 2 tablespoons juice concentrate mixture , the onions , parsley , lime juice and salt', 'add chopped oranges and grapefruit', 'stir gently', 'cover and chill at least 30 minutes to blend flavors', 'remove meat from bag', 'discard marinade', 'place meat on the grill rack of an uncovered grill', 'grill directly over medium coals , turning once , allowing 12 to 14 minutes for medium-rare', 'thinly slice meat', 'if desired , garnish with jalapenos and grapefruit slices', 'serve with salsa and warmed tortillas', 'to broil: place steak on the unheated rack of a broiler pan', 'broil 3 inches from heat for 6 minutes', 'to warm tortillas , stack them and wrap in foil', 'heat in a 350 degree oven for 10 minutes'", "'frozen orange juice', 'jalapeno pepper', 'black pepper', 'paprika', 'water', 'flank steaks', 'green onion', 'parsley', 'lime juice', 'sa