INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("5436", "beef gravy base", "", "0", "'15-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'cuisine', 'preparation', 'occasion', 'north-american', 'condiments-etc', 'beef', 'american', 'dietary', 'meat', 'number-of-servings'", "592.2, 49.0, 115.0, 65.0, 47.0, 100.0, 17.0", "7", "'combine dry milk powder , flour , bouillon granules and thyme', 'cut in butter or margarine and kitchen bouquet until pieces resemble cornmeal', 'store in tightly covered container in refrigerator up to two months', 'beef gravy: in saucepan slowly blend 1 cup cold water into 1 / 2 cup beef gravy base', 'cook and stir until mixture is thickened and bubbly', 'cook 1 minute more', 'makes 1 cup'", "'nonfat dry milk powder', 'all-purpose flour', 'beef bouillon granules', 'dried thyme', 'butter', 'kitchen bouquet'", "6", "https://www.food.com/recipe/5436");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("5437", "lemon poppy seed pound cake", "the lemon syrup on this cake makes it particularly moist and delicious!!", "0", "'15-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'cuisine', 'preparation', 'occasion', 'north-american', 'desserts', 'fruit', 'american', 'oven', 'dinner-party', 'cakes', 'dietary', 'low-sodium', 'low-in-something', 'equipment'", "408.7, 34.0, 103.0, 5.0, 10.0, 63.0, 15.0", "25", "'preheat the oven to 350 degrees f', 'grease a 4-cup or six-cup loaf pan , line the bottom with parchment or waxed paper , and grease and flour the paper', 'or grease and flour a fluted tube pan', 'lightly combine the milk , eggs , and vanilla in a medium-size bowl', 'in a large mixing bowl , combine the cake flour , sugar , baking powder , and salt', 'add the lemon zest and poppy seeds', 'mix on low speed for 30 seconds to blend', 'then add the butter and half the egg mixture', 'mix on low speed until the dry ingredients are moistened', 'increase the speed to medium , and beat for 1 minute', 'scrape down the sides of the bowl', 'gradually add the remaining egg mixture in two batches , beating for 20 seconds after each addition , and scraping down the sides of the bowl as necessary', 'spoon the batter into the prepared pan , and smooth the surface with a spatula', 'the batter will be almost 1 / 2 inch from the top of a 4-cup loaf pan', 'bake , covering the pan loosely with buttered aluminum foil after 30 minutes to prevent over browning , until a toothpick inserted into the center comes out clean , 55 to 65 minutes', 'to get an attractive split down the middle of the crust , wait until the natural split is about to develop , and then use a lightly greased sharp knife to make a shallow mark about 6 inches long down the middle of the cake', 'when the cake splits , it will open along the mark', 'shortly before the cake is done , prepare the lemon syrup: in a small pan over medium heat , stir the sugar and lemon juice together until the sugar has dissolved', 'as soon as the cake comes out of the oven , place the pan on a wire rack , poke the cake all over with a wire tester or wooden skewer , and brush it with half the syrup', 'cool the cake in the pan for 10 minutes', 'loosen the sides of the cake with a spatula , and invert it onto a greased wire rack', 'poke the bottom of the cake with the wire tester , brush it with some syrup , and re-invert it onto a greased wire rack', 'brush the sides with the remaining syrup and allow the cake to cool completely', 'store the cake for 24 hours , tightly wrapped in plastic wrap', 'serve at room temperature'", "'milk', 'eggs', 'vanilla extract', 'cake flour', 'sugar', 'baking powder', 'salt', 'lemon, zest of', 'poppy seeds', 'unsalted butter', 'fresh lemon juice'", "11", "https://www.food.com/recipe/5437");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("5440", "buche de noel iii", "recipe by:  barb schaller & betty crocker", "120", "'weeknight', 'time-to-make', 'course', 'cuisine', 'preparation', 'occasion', 'desserts', 'french', 'oven', 'european', 'dinner-party', 'heirloom-historical', 'holiday-event', 'cakes', 'dietary', 'christmas', 'equipment', '4-hours-or-less'", "394.5, 25.0, 184.0, 8.0, 8.0, 36.0, 19.0", "28", "'heat oven to 375 degrees', 'line jelly roll pan , 15-1 / 2x10-1 / 2x1"" , with waxed paper or baking parchment', 'grease', 'mix flour , cocoa , baking powder , and salt', 'set aside', 'beat eggs in small mixer bowl on high speed until very thick and lemon colored , 3-5 minutes', 'pour eggs into large mixer bowl if using a small one , and gradually beat in granulated sugar', 'beat in water and vanilla on low speed', 'gradually add flour mixture , beating just until batter is smooth', 'pour into pan , spreading batter to corners', 'bake until wooden pick inserted in center comes out clean , 12-15 minutes', 'loosen cake from edges of pan', 'invert on towel generously sprinkled with powdered sugar', 'carefully remove paper', 'trim stiff edges from cake if necessary', 'while hot , roll cake and towel from narrow end', 'cool on a wire rack for 30-60 minutes', 'beat whipping cream , 2 tablespoons of sugar , and the coffee , in chilled bowl until stiff', 'unroll cake carefully', 'spread whipped coffee cream filling over cake , reserving about 3-4 tablespoons in a small dish', 're-roll , using towel to help , if necessary', 'cut a piece of the roll from one end , at an angle - maybe an inch at the narrow part to 2 inches at the wide part of the slice , and set aside for a moment while you begin to frost the log with the chocolate icing', 'make the icing by mixing the cocoa and margarine , then stirring in the powdered sugar , flavor , and hot water to a nice spreading consistency', 'set the waiting slice atop the frosted log - the icing should hold it in place sufficiently , or use a toothpick if you think you need to - to resemble a cut branch stub', 'ice those sides , too , blending into the main log part for a realistic look', ""use the reserved coffee whipped cream to cover the ends of the log and the top of the 'cut branch'"", ""now's the fun: using a fork -- i like one with tines rather apart -- draw the bark on the tree - just pull the fork through the soft frosting"", 'rinse the fork and draw the circular rings on the ends ,'", "'all-purpose flour', 'cocoa', 'baking powder', 'salt', 'eggs', 'granulated sugar', 'water', 'vanilla extract', 'heavy whipping cream', 'sugar', 'instant coffee', 'margarine', 'powdered sugar', 'hot water'", "14", "https://www.food.com/recipe/5440");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("5447", "solo chicken breast and bell pepper", "consider chicken breasts as blank canvas, much in the same way as you might use rice. it does not have an intrusive taste of it's own, but white chicken meat provides a nutritious, inexpensive source of protein to use as a base on which you can build a variety of interesting meals.  here is a quick meal for one that requires only one skillet and a second pan to make the rice or pasta.", "20", "'30-minutes-or-less', 'time-to-make', 'main-ingredient', 'preparation', 'for-1-or-2', 'very-low-carbs', 'poultry', 'rice', 'easy', 'chicken', 'dietary', 'low-carb', 'low-in-something', 'meat', 'chicken-breasts', 'pasta-rice-and-grains', 'number-of-servings', '3-steps-or-less'", "485.0, 44.0, 9.0, 52.0, 56.0, 20.0, 2.0", "3", "'slice the chicken breast and the bell pepper into thin slices and saute in the olive oil until the chicken turns white and the peppers become limp', 'add the rest of the ingredients and cook over a medium heat until the chicken is completely cooked through , about 10 minutes', 'serve over cooked rice or pasta'", "'boneless skinless chicken breast', 'green bell pepper', 'olive oil', 'dried basil', 'garlic clove', 'salt', 'lemon juice', 'white wine'", "8", "https://www.food.com/recipe/5447");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("5448", "solo chicken breast teriyaki style", "", "30", "'30-minutes-or-less', 'time-to-make', 'main-ingredient', 'preparation', 'for-1-or-2', 'poultry', 'chicken', 'dietary', 'meat', 'chicken-breasts', 'number-of-servings'", "730.8, 9.0, 40.0, 172.0, 70.0, 4.0, 8.0", "10", "'slice chicken length wise into four pieces , and thread onto two thin sticks or sharpened chop sticks', 'mix the rest of the ingredients and use to marinate the chicken in a shallow dish for at least two hours', 'turn the meat at least once after the first hour', 'broil 5 to 6 inches from heat , turning once , or on a charcoal grill for about 10 minutes', 'heat marinade until it starts to bubble and pour over cooked meat after it has been removed from the sticks', 'serve over rice', '1 / 3 cup of raw rice grains , 2 / 3 cup of water', 'heat until it just starts to boil , cover and reduce flame to the lowest level', 'cook for 15 minutes without lifting the cover , and then let sit for an additional 5 minute with no heat before serving', 'this meal will go nice with a lettuce salad or a small side dish of vegetables'", "'boneless skinless chicken breast', 'gingerroot', 'garlic', 'soy sauce', 'dry sherry', 'sugar', 'cooking oil'", "7", "https://www.food.com/recipe/5448");
