INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("5450", "solo baked chicken breast and lemon rice", "this solo meal can be doubled, and if you have an instant read thermometer with an oven probe, the dish can be ignored until you are summoned to the kitchen. serve with a green salad or maybe my pickel dilly green beans. cooking is a creative sport.", "50", "'weeknight', '60-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'for-1-or-2', 'poultry', 'rice', 'oven', 'chicken', 'dietary', 'low-sodium', 'low-in-something', 'meat', 'chicken-breasts', 'pasta-rice-and-grains', 'equipment', 'number-of-servings'", "719.2, 38.0, 6.0, 10.0, 66.0, 75.0, 22.0", "8", "'preheat oven 375 degree put 1 tablespoon butter into an oven proof dish stir in the rice until all the grains are coated', 'add the wine', 'cut the lemon in half and squeeze the juice from half the lemon into a dish with the remaining melted butter and the basil', 'other herbs such as dill , or oregano could be used instead', 'coat the chicken breast with the butter / lemon / herb sauce on both sides and place on top of the rice and wine', 'slice the remaining half of the lemon into thin rounds and place on top of the chicken', 'cover with a tight fitting cover or foil and bake for 35 minutes or until all the liquid is absorbed and the chicken has an internal temperature of 175 or 79', 'salt and pepper to taste'", "'boneless skinless chicken breast', 'lemon', 'butter', 'basil', 'rice', 'white wine'", "6", "https://www.food.com/recipe/5450");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("5452", "solo cheddar puff cake", "this will make a nice sized breakfast meal for one person, baked in a 6 inch by 8 inch dish. the recipe can be multiplied by 3 and made in a 9 inch by 13 inch baking dish with 2 inch sides to feed a family of four.", "30", "'30-minutes-or-less', 'time-to-make', 'course', 'preparation', 'for-1-or-2', 'breakfast', 'desserts', 'oven', 'easy', 'cakes', 'dietary', 'equipment', 'number-of-servings'", "565.4, 50.0, 2.0, 32.0, 57.0, 88.0, 12.0", "5", "'preheat oven to 400f', 'place butter in a oven safe 6 by 8 dish and put into oven to melt while mixing the rest of the ingredients', 'add milk to the eggs and beat with a whisk , and add flour and salt', 'beat mixture until smooth , and add the cheese', 'pour into hot dish after butter has melted and bake for 20 minutes'", "'eggs', 'flour', 'milk', 'cheddar cheese', 'salt', 'butter'", "6", "https://www.food.com/recipe/5452");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("5453", "mango muffins", "i was going to make blueberry muffins when i realized that the kids had eaten all my blueberries, so i used mango pulp instead. this fork tender muffin will be very moist. it would be more appropriate at the breakfast table than trying to toss it into a lunch box. i want to thank jeanninemarie in mn for the two most recent pictures that she sent me to include with this recipe.", "33", "'60-minutes-or-less', 'time-to-make', 'course', 'preparation', 'healthy', 'breads', 'oven', 'muffins', 'dietary', 'quick-breads', 'equipment', 'number-of-servings'", "139.0, 6.0, 49.0, 6.0, 4.0, 4.0, 7.0", "7", "'cut and peel two very ripe mangoes and reduce to soft pulp and juice', 'combine flour , baking powder , salt and sugar in a large bowl', 'in a separate bowl combine oil , milk , and egg', 'mix liquid ingredients with dry until just moist and stir in the mango pulp', 'fill greased muffin tins , or paper muffin cups two-thirds full', 'bake at 400f for 15-18 minutes', 'they are done when they are brown on top and a tooth-pick inserted into the center comes out clean'", "'flour', 'baking powder', 'salt', 'sugar', 'vegetable oil', 'milk', 'egg', 'mango'", "8", "https://www.food.com/recipe/5453");
