INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("5456", "smoothy chocolate cookies", "this recipe was inspired by a well known motel chain that features a signature cookie on their pillow. the actual recipe, with one very notable variation can be found on bags of chocolate chips offered by several companies. the preparation time does not include the time needed to freeze the chocolate chips.", "30", "'30-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'occasion', 'for-large-groups', 'drop-cookies', 'desserts', 'oven', 'cookies-and-brownies', 'chocolate', 'dietary', 'equipment', 'number-of-servings'", "133.1, 11.0, 41.0, 2.0, 3.0, 19.0, 5.0", "7", "'place chocolate chips into a freezer until very firm', 'place frozen chocolate chips and chopped walnuts into an electric blender or food processor and chop very briefly', 'cream butter , sugars , eggs and vanilla in a large bowl with and electric mixer on medium speed until light and fluffy', 'mix in flour , baking soda and salt', 'stir in chips and nuts', 'drop by teaspoonfuls 2-inches apart onto ungreased cookie sheets', 'bake at 375f for 10 minutes or until golden brown'", "'butter', 'granulated sugar', 'brown sugar', 'eggs', 'vanilla', 'flour', 'baking soda', 'salt', 'chocolate chips', 'walnuts'", "10", "https://www.food.com/recipe/5456");
