INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("5471", "microwave tuna casserole", "this recipe was designed to meet the limited resources in an office break room or on campus housing.there is enough for two, but leftovers can be refrigerated for several days and reheated.", "14", "'15-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'cuisine', 'preparation', 'north-american', 'for-1-or-2', '5-ingredients-or-less', 'casseroles', 'main-dish', 'pasta', 'seafood', 'american', 'oven', 'easy', 'microwave', 'fish', 'dietary', 'tuna', 'pasta-rice-and-grains', 'saltwater-fish', 'equipment', 'number-of-servings'", "745.2, 55.0, 45.0, 87.0, 70.0, 92.0, 23.0", "6", "'put the macoroni from the box into a microwave popcorn maker -- i use a anchor hocking model', 'add enough water to cover the noodles', 'cook noodles on high for 10 minutes or until tender -- then drain', 'return the drained noodles , butter or margarine , the contents of the cheese package , entire can of cream of mushroom soup', 'the lemon juice and the entire can of tuna', 'heat on high for 1 minute or until hot'", "'macaroni and cheese mix', 'butter', 'cream of mushroom soup', 'tuna fish', 'lemon juice'", "5", "https://www.food.com/recipe/5471");
