INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("5473", "microwave   american  welsh rarebit", "this solo recipe can be a quick snack, that uses leftover bits of cheese and old bread, or with some creativity, a nice looking meal for one or two. the texture on the sauce will depend on how long it is microwaved, and of course your patience. any cheese or combination of cheeses that melt well can be used, and over time you will find your favorite mustard. cooking is a creative sport.", "8", "'15-minutes-or-less', 'time-to-make', 'course', 'cuisine', 'preparation', 'occasion', 'north-american', 'for-1-or-2', 'lunch', 'main-dish', 'snacks', 'american', 'easy', 'european', 'microwave', 'beginner-cook', 'dietary', 'welsh', 'equipment', 'number-of-servings'", "342.4, 37.0, 1.0, 32.0, 8.0, 74.0, 7.0", "5", "'melt the butter and mustard in a microwave safe bowl on high for 15 seconds , add salt and pepper , beer and cheese and microwave on high for at least 30 seconds', 'remove the bowl and stir until the cheese has has achieved the desired consistency', 'taste and add additional salt and pepper if required', 'pour over plain toast or for a lunch , slices of ham and tomatoes may be added', 'serve immediately , with the rest of the beer'", "'butter', 'mustard', 'pepper', 'salt', 'beer', 'velveeta american cheese spread', 'italian bread'", "7", "https://www.food.com/recipe/5473");
