INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("6555", "red beans with rice", "i found this recipe on foodnetwork.com and updated my adopted recipe to include these additions.   recipe courtesy emeril lagasse, 2001 show:  emeril live  episode:  emeril's creole creation", "170", "'time-to-make', 'main-ingredient', 'cuisine', 'preparation', 'north-american', 'beans', 'pork', 'rice', 'american', 'cajun', 'southern-united-states', 'dietary', 'meat', 'pasta-rice-and-grains', '4-hours-or-less'", "544.9, 36.0, 26.0, 44.0, 70.0, 41.0, 15.0", "15", "'in a large bowl or pot add the beans and cover with water by 2 inches', 'soak for 8 hours or overnight', 'drain and set aside', 'in a large pot over medium-high heat melt the bacon grease', 'add the tasso or ham and cook , stirring , for 1 minute', 'add the onions , celery and bell peppers to the grease in the pot', 'season with the salt , pepper , and cayenne , and cook , stirring , until the vegetables are soft , about 4 minutes', 'add the bay leaves , parsley , thyme , sausage , and ham hocks , and cook , stirring , to brown the sausage and ham hocks , about 4 minutes', 'add the garlic and cook for 1 minute', 'add the beans and stock or water , stir well , and bring to a boil', 'reduce the heat to medium-low and simmer , uncovered , stirring occasionally , until the beans are tender and starting to thicken , about 2 hours', 'remove from the heat and with the back of a heavy spoon , mash about 1 / 4 of the beans against the side of the pot', 'continue to cook until the beans are tender and creamy , 15 to 20 minutes', 'remove from the heat and remove the bay leaves', 'serve over rice and garnish with green onions'", "'dried red beans', 'bacon grease', 'tasso', 'yellow onions', 'celery', 'green bell pepper', 'salt', 'black pepper', 'cayenne', 'bay leaves', 'fresh parsley', 'fresh thyme', 'smoked sausage', 'smoked ham hock', 'garlic', 'chicken s