INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("6578", "shrimp in a light curry sauce", "i have not made this dish, but it sounds delicious!  this is an adopted recipe.", "0", "'15-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'very-low-carbs', 'condiments-etc', 'seafood', 'shrimp', 'dietary', 'low-sodium', 'low-carb', 'low-in-something', 'shellfish'", "386.2, 33.0, 10.0, 14.0, 75.0, 61.0, 2.0", "10", "'shell and de-vein the shrimp', 'rinse well , pat dry , and set aside', 'in a skillet over medium heat , melt the butter', 'saute the onion for about 7 minutes', 'add the red chile pepper and cook , stirring , for 1 minute', 'add the shrimp , then season to taste with salt and freshly ground pepper', 'cook , stirring often , about three minutes', 'add the cardamom and cumin and stir , then add the lime juice , sour cream , and yoghurt', 'bring to a gentle boil , stirring , then remove from heat', 'serve immediately , sprinkled with chopped coriander , with saffron rice'", "'raw shrimp', 'butter', 'onion', 'dried red pepper', 'ground cardamom', 'cumin', 'lime, juice of', 'sour cream', 'plain yogurt', 'fresh coriander', 'salt & pepper'", "11", "https://www.food.com/recipe/6578");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("6580", "spicy green beans  masaledar sem", "", "12", "'15-minutes-or-less', 'time-to-make', 'main-ingredient', 'preparation', 'occasion', 'beans', 'dietary', 'spicy', 'taste-mood'", "209.9, 23.0, 17.0, 21.0, 11.0, 9.0, 5.0", "18", "'trim the green beans and cut them crosswise at 1 / 4 in intervals', 'put the ginger and garlic into the container of an electric blender or food processor', 'add 1 / 3 of the water and blend until fairly smooth', 'heat the oil in a wide , heavy saucepan over a medium flame', 'when hot , put in the cumin seeds', 'five seconds later , put in the crushed chili', 'as soon as it darkens , pour in the ginger-garlic paste', 'stir and cook for about a minute', 'put in the coriander', 'stir a few times', 'now put in the chopped tomatoes', 'stir and cook for about 2 minutes , mashing up the tomato pieces with the back of a slotted spoon as you do so', 'put in the beans , salt and the remaining water', 'bring to simmer', 'cover , turn heat to low and cook for about 8-10 minutes or until the beans are tender', 'remove the cover', 'add the lemon juice , roasted cumin , and a generous amount of freshly ground pepper', 'turn heat up and boil away all of the liquid , stirring the beans gently as you do so'", "'fresh green beans', 'ginger', 'garlic', 'water', 'vegetable oil', 'whole cumin seeds', 'dried hot red chilies', 'coriander', 'chopped tomato', 'salt', 'lemon juice', 'roasted cumin'", "12", "https://www.food.com/recipe/6580");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("6582", "basmati rice seasoned with garam masala", "this is a recipe that's delicious and will go well with your favorite curry.", "30", "'weeknight', '30-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'cuisine', 'preparation', 'occasion', 'side-dishes', 'rice', 'asian', 'indian', 'oven', 'potluck', 'dinner-party', 'comfort-food', 'pasta-rice-and-grains', 'long-grain-rice', 'taste-mood', 'to-go', 'equipment', 'presentation', 'served-hot'", "304.1, 14.0, 5.0, 3.0, 10.0, 26.0, 16.0", "8", "'preheat oven to 350f', 'in a medium stove-to-oven pot melt butter over medium heat', 'add chopped onions , garlic and garam masala and cook 3 to 5 minutes , stirring until onions are softened', 'add rice and stir until well coated', 'add vegetable stock , salt and pepper to taste', 'bring to a boil', 'cover and bake in the oven until the rice is tender and the stock is absorbed , about 20 to 25 minutes', 'let stand , covered for 5 minutes before serving'", "'basmati rice', 'butter', 'onion', 'garlic', 'garam masala', 'salt & freshly ground black pepper', 'vegetable broth'", "7", "https://www.food.com/recipe/6582");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("6583", "curried lamb", "we don't have lamb for dinner very often - usually when we do, it's a traditional leg of lamb when my mother is visiting.  this, however, is a nice, simple, and easily-made variation for when you find less expensive cuts of lamb.", "0", "'weeknight', '15-minutes-or-less', 'time-to-make', 'main-ingredient', 'preparation', 'occasion', 'very-low-carbs', 'lamb-sheep', 'dietary', 'low-sodium', 'low-calorie', 'low-carb', 'low-in-something', 'meat'", "428.4, 32.0, 6.0, 17.0, 105.0, 29.0, 1.0", "7", "'soak onion in water until soft about 5 minutes', 'heat oil in large skillet over medium high heat , and add the onion and saute until golden about 4 minutes', 'reduce heat to low , then add coriander , cumin , cardamom , ginger , tumeric , garlic powder , freshly ground black pepper , and ground red pepper , and stir 1 minute', 'add the cubed lamb meat to skillet , then increase the temperature to medium-high and cook , stirring frequently , until meat is evenly browned , about 10 to 15 minutes', 'add stock and salt , reduce temperature to medium , cover , and simmer until meat is tender , about 20 minutes', 'uncover and simmer an additional approx 20 minutes until the sauce is thickened to desired consistency', 'stir in yogurt and lemon juice and serve immediately over cooked rice'", "'dried onion', 'water', 'vegetable oil', 'coriander', 'cumin', 'cardamom', 'ginger', 'turmeric', 'garlic powder', 'pepper', 'red pepper', 'lamb stew meat', 'beef stock', 'salt', 'plain yogurt', 'fresh lemon juice', 'rice'", "17", "https://www.food.com/recipe/6583");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("6584", "rogan josh", "", "0", "'15-minutes-or-less', 'time-to-make', 'main-ingredient', 'cuisine', 'preparation', 'beef', 'lamb-sheep', 'asian', 'indian', 'dietary', 'meat'", "589.9, 70.0, 12.0, 31.0, 58.0, 50.0, 7.0", "24", "'put the ginger , garlic , and 4 tablespoons water in blender', 'blend well until you have a smooth paste', 'heat the oil in a wok to medium high heat', 'brown the meat cubes in several batches and set to one side', 'put the cardamom , bay leaves , cloves , peppercorns , and cinnamon into the same hot oil', 'stir once and wait until the cloves swell and the bay leaves begin to take on color', 'now put in the onions', 'stir and fry for about 5 minutes or until the onions turn a medium brown color', 'put in the ginger garlic paste and stir for 30 seconds', 'add the ground coriander , cumin , paprika and cayenne and fry for 30 seconds until the ""raw"" smell of the spices disappears', 'add a splash of hot water if the mixture starts to stick , this will prevent thhe spices from burning', 'add the fried meat cubes and juices and stir for 30 seconds', 'now put in 1 / 6 of the yoghurt', 'stir and fry for 30 seconds or until the yoghurt is well blended', 'add the remaining yoghurt , a tablespoon at a time , in the same way', 'stir and fry for another 3-4 minutes', 'now add the rest of the broth', 'bring the contents of the pot to a boil , scraping in all browned spices on the sides and bottom of the pot', 'cover , turn heat to low and simmer for about an hour if lamb and two if beef', 'every 10 minutes or so , give the meat a good stir', 'when the meat is tender , take off the lid , turn the heat up to medium and boil away some of the liquid', 'you should end up with a tender meat in a thick , reddish brown sauce', 'all the fat that collects in the pot may be spooned off the top', 'sprinkle the garam masala and black pe