INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("6651", "true garlic bread", "this is an adopted recipe that looks wonderful to me--but since i have not yet actually tried it, please feel free to offer any suggestions or changes via a review should you decide to try it. thanks!", "215", "'weeknight', 'time-to-make', 'course', 'preparation', 'healthy', 'breads', 'oven', 'vegetarian', 'dietary', 'equipment', '4-hours-or-less'", "631.0, 27.0, 2.0, 97.0, 36.0, 50.0, 34.0", "35", "'cream together the garlic puree and butter', 'combine the yeast with 1 / 2 cup warm water in large bowl', 'stir with a fork or small whisk', 'add an additional 2 1 / 2 cups water', 'add salt', 'stir in the flour , 1 c at a time , beginning with the whole wheat', 'use a whisk until the dough becomes stiff , then switch to a wooden spoon', 'turn the dough onto a well floured work surface', 'knead rhythmically for 10 to 15 minutes , until the dough is smooth , springy , nonsticky , and elastic', 'add more flour as you knead if necessary', 'the dough is ready if you can poke to fingers into it and the resulting indentations spring back', 'cover the dough with a cloth and let rest while you wash , dry and generously butter the bowl', 'knead the dough a few more turns , then form it into a ball and place it in the bowl', 'turn it to coat with butter', 'cover the bowl and put it in a warm , draft-free place until the dough has doubled in bulk , about 1 1 / 2 hours', 'it has risen sufficiently when you can gently poke a finger into the dough and the hole reamins', 'when doubled , flour your fist and punch the dough down', 'knead it a few times and then let it rest', 'sprinkle 1 large or 2 small baking sheets with a liberal amount of cornmeal', 'divide the dough into 3 equal parts', 'while you work with 1 piece , keep the other 2 covered', 'flour your work surface', 'with a rolling pin , roll each piece of dough into a rectangle approximately 14-inches long x 7-inches wide', 'spread it with softened garlic butter', 'roll the long edge toward the opposite long edge , as if you were rolling up a rug', 'pinch ends closed', 'place loaves on the baking sheets', 'with a sharp knife or razor blade , slash the loaves lightly at 2-inch intervals', 'cover with a cloth and place in a warm draft-free place to rise until doubled , about 1 / 2 hour', 'meanwhile preheat oven to 400 degrees f', 'bake for 35 to 40 minutes with a pan of boiling water on the oven floor', 'spray loaves with water several times during the baking process', 'to test for doneness , rap the loaf with your knuckles', 'the loaf should sound hollow', 'cool on wire racks , but the loaves are delicious eaten warm right out of the oven'", "'garlic heads', 'unsalted butter', 'dry yeast', 'water', 'kosher salt', 'whole wheat flour', 'unbleached all-purpose flour', 'cornmeal'", "8", "https://www.food.com/recipe/6651");
