INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("6669", "chicken and sausage in tomato sauce", "", "65", "'weeknight', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'poultry', 'chicken', 'dietary', 'low-carb', 'low-in-something', 'meat', 'chicken-thighs-legs', '4-hours-or-less'", "1164.5, 133.0, 39.0, 125.0, 139.0, 138.0, 7.0", "18", "'heat oil in a large heavy skillet', 'add the garlic and saute until light golden', 'remove the garlic and reserve', 'prick the sausages in several places and then sear them in the hot oil', 'remove and set aside , covered', 'brown chicken well on all sides in the same skillet', 'remove and set aside , covered', 'pour 1 t of the drippings into a large dutch oven or casserole', 'discard remaining drippings or save them for another use', 'crush the tomatoes with your hands and add them , with their juice , to the drippings', 'add the tomato paste , salt , freshly ground pepper , basil and reserved garlic', 'simmer , uncovered , for 15 minutes', 'add the sausage and simmer , covered , for 20 minutes', 'add the chicken , cover , and simmer for 20 minutes more', 'meanwhile , deglaze the skillet with the wine', 'stir wine into the sauce and simmer for a final 10 minutes', 'sprinkle with parsley', 'serve with pasta and freshly grated parmesan cheese'", "'olive oil', 'garlic', 'sweet italian sausage links', 'hot italian sausage links', 'chicken legs', 'chicken thighs', 'tomatoes', 'tomato paste', 'salt and pepper', 'dried basil', 'dry red wine', 'fresh parsley'", "12", "https://www.food.com/recipe/6669");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("6670", "sourdough banana bread", "i enjoy finding interesting different things to make from alaskan sourdough starter (see recipe #7020 for instance), so i was so happy when i was able to adopt this recipe!  :)", "70", "'time-to-make', 'course', 'preparation', 'sourdough', 'breads', 'oven', 'dietary', 'yeast', 'equipment', '4-hours-or-less'", "287.5, 21.0, 76.0, 11.0, 7.0, 13.0, 12.0", "8", "'cream together the shortening and sugar , add egg and mix until blended', 'stir in bananas and sourdough starter', 'add orange peel or vanilla', 'stir flour and measure again with salt , baking powder and soda', 'add flour mixture and walnuts to the first mixture , stirring until just blended', 'pour into greased 9 x 5-inch loaf pan', 'bake in 350 degree oven for 1 hour or until toothpick comes out clean', 'cool to cold before slicing'", "'shortening', 'sugar', 'egg', 'mashed banana', 'sourdough starter', 'unbleached flour', 'salt', 'baking powder', 'baking soda', 'walnuts', 'vanilla'", "11", "https://www.food.com/recipe/6670");
