INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("6676", "sunday black bean soup", "", "122", "'weeknight', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'occasion', 'soups-stews', 'beans', 'dietary', 'black-beans', '4-hours-or-less'", "384.0, 4.0, 34.0, 9.0, 41.0, 3.0, 21.0", "11", "'to soak beans quickly , cover them with generous amount of water , cover pot , bring water to boil and boil 2 minutes', 'remove cover and allow beans to sit in the liquid for one hour', 'drain', 'in hot oil , saute carrots , onions , celery , and garlic until onions are golden', 'add cumin and jalepenos', 'when beans are ready to cook , add ham hocks to beans along with sauted vegetables , and chicken broth and enough water to cover', 'cook for 1 1 / 2 to 2 hours , or until tender', 'drain the bean mixture and reserve cooking liquid', 'puree beans and vegetables', 'add sherry and lime juice and enough of the reserved liquid to make a thick soup', 'can be served with chopped corriander or sour cream or lemon slice'", "'dried black beans', 'smoked ham hocks', 'chicken stock', 'carrots', 'onions', 'celery', 'jalapeno pepper', 'garlic', 'cumin', 'dry sherry', 'lime'", "11", "https://www.food.com/recipe/6676");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("6679", "dillbrot  dill bread", "this is one of the tasty recipes i received in the great recipe adoption of feb 2005.  :)", "0", "'15-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'healthy', 'breads', 'eggs-dairy', 'vegetables', 'oven', 'dietary', 'yeast', 'equipment'", "385.1, 11.0, 32.0, 34.0, 31.0, 16.0, 21.0", "9", "'dissolve yeast in warm water', 'combine all ingredients in a mixing bowl , except add the flour a little at a time', 'beat until well mixed and mixture is stiff but not heavy', 'cover and let rise in a warm place until doubled', 'punch down and put dough in a bread pan , or arrange in a round shape on a greased cookie sheet', 'let rise again', 'preheat oven to 350 degrees f', 'bake for 30 to 45 minutes at 350 degrees f', 'while warm , brush loaf with soft butter , sprinkle well with salt'", "'yeast', 'water', 'cottage cheese', 'sugar', 'onion', 'butter', 'egg', 'salt', 'dill seeds', 'flour'", "10", "https://www.food.com/recipe/6679");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("6680", "chicken braised with 40 cloves of garlic", "", "90", "'weeknight', 'time-to-make', 'main-ingredient', 'preparation', 'occasion', 'poultry', 'oven', 'chicken', 'dietary', 'low-sodium', 'low-calorie', 'low-carb', 'low-in-something', 'meat', 'chicken-thighs-legs', 'equipment', '4-hours-or-less'", "271.6, 19.0, 7.0, 4.0, 55.0, 14.0, 3.0", "10", "'preheat oven to 375 degrees f', 'place all the ingredients in a deep , heavy pot that can be covered', 'combine everything very well with your hands', 'seal the pot very tightly with foil', 'place a cover over the foil', 'pot must be very well sealed so that no juices or steam can escape', 'bake for 1 1 / 2 hours', 'do not open the pot during this time', 'serve piping hot , with good crusty bread for mopping up the juices and garlic', 'open the pot at the table , so that the diners may get the full benefit of the marvelous fragrance that explodes out of the vessel'", "'garlic', 'chicken legs-thighs', 'onions', 'olive oil', 'fresh parsley', 'dried tarragon', 'allspice', 'cinnamon', 'salt & pepper', 'cognac', 'dry white vermouth'", "11", "https://www.food.com/recipe/6680");
