INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("6689", "french toast cheddar sandwiches", "these sandwiches will make you never want just a plain old grilled cheese sandwich again.  great sandwich to make when you have thick sliced bread, but you don't have to have it...plain bread will do fine!!!!", "15", "'15-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'cuisine', 'preparation', 'breakfast', 'lunch', 'main-dish', 'eggs-dairy', 'french', 'european', 'dietary', 'sandwiches', 'high-calcium', 'high-in-something'", "371.9, 35.0, 10.0, 37.0, 29.0, 64.0, 8.0", "16", "'set out a heavy skillet or cast iron griddle', 'beat the eggs slightly in a pie tin or shallow bowl and add the milk or cream and salt , set aside', 'spread the bread slices out on a flat working surface', 'spread one side of four slices of bread lightly with the prepared mustard', 'top each with a slice of cheddar cheese', 'butter the remaining four slices of bread and top each cheese slice with bread , butter side down', 'heat the butter in the skillet or on the griddle', 'carefully dip each sandwich into the egg mixture , coating both sides', 'allow the excess egg mixture to drain back into the bowl', 'dip only as many sandwiches as will lie flat in the skillet or griddle', 'cook over low heat until browned', 'turn and brown the other sides', 'repeat for the remaining sandwiches and if necessary , add more butter to the skillet or griddle to prevent sticking', 'or you can place the sandwiches , after dipping , on a well greased baking sheet and brown in the oven at 450 degrees f', 'for 8 to 10 minutes', 'serve hot'", "'eggs', 'milk', 'salt', 'white bread', 'mustard', 'cheddar cheese', 'butter'", "7", "https://www.food.com/recipe/6689");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("6690", "cheddar stuffed franks in buns", "", "0", "'15-minutes-or-less', 'time-to-make', 'main-ingredient', 'preparation', 'eggs-dairy', 'dietary'", "1556.3, 177.0, 39.0, 134.0, 147.0, 298.0, 18.0", "8", "'slit the frankfurters almost through lengthwise', 'mix the pickle relish and prepared mustard together', 'cut the cheddar cheese into 12 4 1 / 2 by 1 / 2-inch strips', 'put one strip of cheddar cheese and about 2 tsps of the relish mixture into each frankfurter and set aside', 'panbroil the bacon until partially cooked and still limp and drain', 'starting at one end , securing the bacon with a wooden pick , wrap each frankfurter and secure the other end with a wooden pick also', 'set the oven temperature to broil and arrange the bacon wrapped frankfurters on the broiler rack with the tops 3-inches below the heat source and broil until the bacon in cooked , turning once', 'serve hot in the buns'", "'frankfurters', 'sweet pickle relish', 'mustard', 'medium sharp cheddar', 'bacon', 'hot dog buns'", "6", "https://www.food.com/recipe/6690");
