INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("6702", "cowboy s brisket", "adopted from the recipezaar account on 9/15/2006. uncertain of original contributor.", "240", "'weeknight', 'time-to-make', 'main-ingredient', 'preparation', 'occasion', 'beef', 'oven', 'dietary', 'meat', 'equipment', '4-hours-or-less'", "79.8, 0.0, 26.0, 0.0, 4.0, 0.0, 5.0", "23", "'with a long thin , sharp knife , make slits in the meat and insert the slivers of garlic', 'place the meat in a bowl , spread 1 sliced onion and the crushed garlic over the meat , and pour in the vinegar', 'marinate for 6 hours at room temperature or overnight in the refrigerator , turning several times', 'when ready to cook , preheat the oven to 350 degrees f', 'heat the bacon fat in a deep , heavy skillet large enough to hold the brisket', 'remove the brisket from the marinade and discard the onion and vinegar', 'dry with paper towels', 'brown the meat well on all sides', 'remove brisket to a platter', 'in the fat remaining in the skillet , saute the remaining sliced onions until deeply browned', 'pour in 1 / 2 cup coffee', 'bring to a boil , stirring and scraping the bottom of the skillet to loosen the browned bits', 'spread the onions and liquid from the skillet in a shallow baking dish', 'place the brisket on the onions', 'season with salt and freshly ground pepper to taste', 'pour in the remaining coffee and water', 'cover tightly with foil and place in oven for 1 / 2 hour', 'turn the oven down to 250 degrees f', 'and bake for an additional 2 hours or until meat is very tender', 'slice the brisket thinly against the grain', 'skim the fat from the pan liquid', 'return the meat slices to the pan', 'serve at once or refrigerate for later use'", "'first cut briskets of beef', 'garlic cloves', 'onions', 'apple cider vinegar', 'bacon fat', 'strong black coffee', 'salt & pepper', 'water'", "8", "https://www.food.com/recipe/6702"