INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("6711", "cheddar bread ring", "this recipe is one of the recipes i adopted from the recipezaar abandoned recipe adoptions.", "0", "'weeknight', '15-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'occasion', 'breads', 'eggs-dairy', 'oven', 'dietary', 'equipment'", "301.5, 17.0, 13.0, 16.0, 22.0, 35.0, 12.0", "17", "'combine 1 1 / 2 cups of the flour , the sugar , undissolved yeast and salt thoroughly in a large bowl', 'heat the milk and butter together until very warm', 'gradually add to the dry ingredients and beat at medium speed on an electric mixer for 2 minutes , scraping the bowl occasionally', 'add 1 / 2 cup of the flour and the cheese', 'beat fir 2 minutes on high speed on the mixer , scraping the bowl occasionally', 'stir in enough additional flour to make a stiff but light dough', 'turn the dough out onto a lightly floured surface and knead until smooth and elastic , 5 to 8 minutes', 'place in a greased bowl , turning once to grease the top', 'cover with a dishtowel that has been soaked in hot water and then wrung out until almost dry', 'let rise in a warm place until doubled in bulk , about 1 hour', 'punch the dough down and turn out on a lightly floured surface and shape into a 20-inch rope', 'place seam side down in a buttered 6 1 / 2 cup ring mold , pinching the ends together', 'cover and let rise in warm place until nearly doubled in bulk , about 35 to 40 minutes', 'bake in a preheated 350 degree f', 'oven for 25 to 30 minutes', 'remove from the ring mold', 'crust will become crisp when cool if you do not'", "'bread flour', 'sugar', 'active dry yeast', 'salt', 'milk', 'butter', 'cheddar cheese'", "7", "https://www.food.com/recipe/6711");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("6713", "flank steak with garlic wine sauce", "", "6", "'15-minutes-or-less', 'time-to-make', 'course', 'preparation', 'very-low-carbs', 'condiments-etc', 'dietary', 'low-sodium', 'low-carb', 'low-in-something'", "444.6, 39.0, 2.0, 4.0, 73.0, 65.0, 1.0", "18", "'sprinkle flank steak with salt and a generous amount of freshly ground black pepper', 'heat a wide heavy skillet', 'do not add fat', 'when hot , cook seasoned steak until seared and well browned on each side', 'reduce heat and add 2 t butter', 'cook 3 to 5 minutes on each side', 'for best results , the meat should be quite rare', 'remove the meat from the pan and keep warm', 'pour off the fat in the skillet and add the scallions and red wine', 'bring to a boil and whisk in the garlic puree', 'boil until the wine is reduced by half , thickened , and syrupy', 'as it boils , scrape up the browned bits in the skillet with a wooden spoon', 'stir in the meat juices that have accumulated unter the flank steak', 'boil for 1 or 2 seconds more', 'remove from the heat', 'gently swirl in the 2 t soften butter so that it incorporates into the wine', 'quickly slice the meat , against the grain , into thin strips', 'arrange the slices on a hot platter , pour sauce down the center of them and serve at once'", "'flank steaks', 'salt & pepper', 'unsalted butter', 'scallions', 'dry red wine', 'garlic head'", "6", "https://www.food.com/recipe/6713");
