INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("6721", "no knead cheddar rolls", "this is an adopted recipe. i shall be trying it out and then post any additional information if needs be. feel free to try it out first and share your comments!", "0", "'weeknight', '15-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'breads', 'eggs-dairy', 'oven', 'dietary', 'equipment'", "331.4, 25.0, 19.0, 20.0, 18.0, 50.0, 12.0", "28", "'place the grated cheese in a small bowl and cover to prevent drying then set aside', 'combine 1 1 / 2 cups unsifted flour , yeast , sugar , and salt in a large mixer bowl , blending thoroughly', 'measure 3 / 4 c of milk , water , and butter into a saucepan and heat until the liquids are warm , 115 to 120 degrees f', 'gradually add the liquids to the dry ingredients in the mixer bowl , beating for 2 minutes at medium speed of the electric mixer , scraping the bowl occasionally', 'add and beat in 1 cup of unsifted flour at high speed', 'beat for 2 minutes , scraping the bowl occasionally', 'mix in enough additional flour to make a soft dough', 'put the dough into a greased deep bowl', 'cover with waxed paper and a clean towel and let stand in a warm place until the dough has doubled , 45 to 60 minutes', 'generously grease several baking sheets', 'melt the butter and set aside', 'punch the dough down wit a fist and turn the dough out onto a lightly floured surface', 'divide the dough into two equal portions', 'set one portion aside', 'roll the dough into a rectangle 16 x 8-inches', 'brush with about one-half of the melted butter', 'sprinkle with about one half of the grated cheddar cheese', 'cut crosswise into 8 equal portions', 'cut into halves lengthwise', 'fold each strip into thirds , lapping each side portion over the center third', 'place the rolls on a baking sheet', 'repeat for the other half of the dough', 'beat the egg yolk with the tbls of milk , slightly', 'brush the tops of the rolls with the egg yolk mixture', 'let rise until doubled , about 30 minutes', 'bake at 425 degrees f', 'for about 8 minutes or until rolls are golden brown', 'serve rolls hot'", "'unbleached flour', 'active dry yeast', 'sugar', 'salt', 'milk', 'water', 'butter', 'cheddar cheese', 'egg yolk'", "9", "https://www.food.com/recipe/6721");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("6722", "california rarebit", "", "1", "'15-minutes-or-less', 'time-to-make', 'main-ingredient', 'preparation', 'occasion', 'very-low-carbs', 'eggs-dairy', 'dietary', 'high-calcium', 'low-carb', 'high-in-something', 'low-in-something'", "392.4, 48.0, 6.0, 19.0, 40.0, 96.0, 1.0", "10", "'melt 1 tbl of butter in the top of a double boiler', 'add the wine and heat , then stir in 2 cups of the cheese', 'heat until melted', 'add a little of the cheese mixture to the beaten egg and then add the egg mixture back into the cheese', 'cook and stir about 1 minute', 'add the worcestershire sauce and basil , set aside keeping the sauce warm', 'saute the mushrooms in the remaining butter until just tender then sprinkle with the garlic powder and remove from the heat', 'arrange toast points or triangles on individual heatproof plates', 'spoon the sauce over the toast then top with the sauteed mushrooms', 'sprinkle with the remaining cheese and broil until bubbly'", "'butter', 'dry white wine', 'monterey jack cheese', 'egg', 'worcestershire sauce', 'basil', 'mushrooms', 'garlic powder'", "8", "https://www.food.com/recipe/6722");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("6723", "panhandle cornbread", "this is another of my adopted recipes.  i have made this recipe for years.  we always called it ", "0", "'15-minutes-or-less', 'time-to-make', 'course', 'preparation', 'occasion', 'breads', 'oven', 'dietary', 'equipment'", "679.0, 80.0, 15.0, 28.0, 32.0, 89.0, 13.0", "10", "'preheat the oven to 400 degrees f', 'and generously grease a 12 cup bundt or 9-inch tube pan', 'set aside', 'in a large bowl , combine the cornmeal and baking powder', 'stir in the cheddar', 'in a medium bowl , beat the eggs , oil , sour cream , corn and chiles together', 'add to the cornmeal mixture', 'stir until just moistened and then spoon the batter into the prepared pan', 'bake for 40 to 50 minutes in the preheated oven until a wooden pick inserted in the center comes out clean', 'cool on a rack for 10 minutes then invert over a serving plate'", "'cornmeal', 'baking powder', 'cheddar cheese', 'eggs', 'vegetable oil', 'sour cream', 'cream-style corn', 'green chili peppers'", "8", "https://www.food.com/recipe/6723");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("6724", "gratin of potatoes and cheese", "--adopted recipe--", "75", "'weeknight', 'time-to-make', 'main-ingredient', 'preparation', 'occasion', 'eggs-dairy', 'potatoes', 'vegetables', 'oven', 'easy', 'cheese', 'dietary', 'low-sodium', 'low-in-something', 'equipment', '4-hours-or-less'", "664.1, 81.0, 6.0, 6.0, 28.0, 162.0, 11.0", "10", "'preheat the oven to 325 degrees f', 'rub the bottom and sides of a gratin dish or 2 quart casserole with the cut sides of the garlic , if you are using the garlic', 'leave the garlic in the dish , if desired , or remove it if you prefer a less garlicky taste', 'peel potatoes and slice paper-thin', 'place a layer of potato slices in dish and sprinkle with some salt and freshly ground pepper and cheese (gruyere is best , but if you are on a budget you may prefer swiss', 'pour some cream over the potatoes', 'repeat layers until all the potatoes , cream , and cheese are used', 'place the pan in the oven , uncovered , with a baking sheet underneath to catch spills and bake for 1 hour or until the top is browned and the cream has cooked down to a thick sauce', 'during the first half hour of cooking , use a broad spatula to push the top layers of potatoes into the cream every once in a while', 'serve hot'", "'garlic cloves', 'idaho potatoes', 'salt & pepper', 'gruyere cheese', 'whipping cream'", "5", "https://www.food.com/recipe/6724");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("6726", "sunrise popovers", "", "95", "'weeknight', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'breads', 'eggs-dairy', 'oven', 'dietary', 'low-carb', 'low-in-something', 'equipment', '4-hours-or-less'", "152.8, 18.0, 0.0, 9.0, 12.0, 22.0, 1.0", "11", "'preheat the oven to 375 degrees f', 'place eight 6-oz custard cups on a large baking sheet', 'spoon 1 1 / 2 tsp of shortening into the bottom of each custard cup and set aside', 'combine the flour and salt in a large bowl , then gradually stir in the milk and water until well blended', 'beat in the eggs , 1 at a time , beating until smooth after each addition', 'fold in the cheddar cheese', 'place the baking sheet with the custard cups in the preheated oven for 3 to 5 minutes until the shortening melts and the custard cups are hot', 'fill the custard cups 1 / 2 to 2 / 3rds full with the batter', 'bake for 45 minutes in the preheated oven , without opening the oven door until the popovers rise and turn golden brown', 'if not golden brown after 45 minutes , bake for an additional 5 minutes', 'serve piping hot'", "'vegetable shortening', 'unbleached flour', 'salt', 'milk', 'water', 'eggs', 'cheddar cheese'", "7", "https://www.food.com/recipe/6726");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("6727", "polka dot quick bread", "i adopted this recipe from recipezaar, and i'm very proud to be it's new owner. i made a few changes from the original recipe and i think my version is perfect!  this quick bread is great for breakfast on the go or as a late night snack with a glass of milk.  i plan on making these next christmas for gifts!  ", "80", "'weeknight', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'occasion', 'breads', 'eggs-dairy', 'fruit', 'oven', 'dietary', 'equipment', '4-hours-or-less'", "376.8, 22.0, 107.0, 16.0, 15.0, 31.0, 18.0", "12", "'preheat the oven to 350 degrees f', 'spray a 9 x 5-inch loaf pan with non-stick cooking spray', 'set aside', 'coarsley chop the cranberries and set aside in a small bowl', 'in a medium bowl , combine the milk , egg , melted butter , and orange juice with a whisk', 'set aside', 'sift the flour , sugar , baking powder , and salt into a large bowl', 'add the chopped cranberries , cheese and nuts to flour mixture and toss to coat cranberries', 'add the milk mixture to the flour mixture and stir until just moistened', 'pour into the prepared loaf pan and bake for about 1 hour until a wooden pick inserted in the center comes out clean', 'cool in the pan on a rack for 10 minutes , then remove from the pan', 'cool to room temperature on the wire rack before slicing'", "'cranberries', 'milk', 'egg', 'butter', 'orange juice', 'unbleached flour', 'sugar', 'baking powder', 'salt', 'cheddar cheese', 'walnuts'", "11", "https://www.food.com/recipe/6727");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("6728", "cheddar braids", "adopted from recipezaar, february 2005. i tried this for the first time feb 20, '05. i used fresh yeast, did not wait until it bubbled, and did most of the mixing by hand, only using my mixer to mix in the butter well. used 7 oz grated edam. dough is soft and a little hard to work with, flavor is excellent, bread is very rich. ", "0", "'weeknight', '15-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'occasion', 'breads', 'eggs-dairy', 'oven', 'dietary', 'equipment'", "494.7, 43.0, 3.0, 32.0, 31.0, 82.0, 14.0", "28", "'pour the warm water into a warm bowl and add the yeast', 'stir to dissolve then let stand until light and puffed , about 5 minutes', 'add 1 1 / 2 cups of the flour , sugar and salt', 'beat with an electric mixer on the lowest speed for 1 minute', 'beat on medium speed for 2 minutes longer', 'add the butter to the yeast mixture and beat for another 1 minute', 'on the lowest speed on the mixer , beat in 1 egg and 1 / 2 cup of flour until well blended , repeating until the 4 eggs are used up and enough flour has been added to make a soft sticky dough', 'continue to beat with the mixer or by hand , until the dough is glossy and elastic and pulls away from the side of the bowl', 'stir in the cheddar cheese by hand', 'cover and let rise in a warm place free from drafts until doubled in bulk , about 2 1 / 2 to 3 hours', 'when the dough has doubled in bulk , punch down and place in the refrigerator for at least 5 hours or better , overnight', 'remove the dough from the refrigerator', 'divide in half and cover and refrigerate the second ball of dough', 'knead the remaining ball of dough on a lightly floured surface until soft and pliable', 'divide the dough into 3 equal parts and roll each piece into a rope 12 to 16-inches long', 'braid the ropes , starting in the middle and working toward each end', 'pinch the ends together so seal them', 'grease a large baking sheet and place the finished braid on one side of the sheet', 'repeat with the refrigerated dough', 'in a small bowl beat the egg and milk together', 'brush the braids with the egg mixture and let the braids rise in a warm place , free from drafts , until dough in bulk , about 1 1 / 2 to 2 hours', 'do not cover', 'midway through the rising time , brush with the egg mixture again', 'preheat the oven to 400 degrees f', 'when fully risen , brush with the egg mixture for a final time and sprinkle evenly with the celery seeds', 'bake for 40 minutes in the preheated oven until a wooden skewer or pick inserted in the braid comes out dry', 'remove from the oven and from the baking sheet', 'cool to room temperature , on wire racks , before slicing'", "'water', 'active dry yeast', 'unbleached flour', 'sugar', 'salt', 'butter', 'eggs', 'cheddar cheese', 'egg', 'milk', 'celery seeds'", "11", "https://www.food.com/recipe/6728");
