INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("6740", "fruit tarts", "", "140", "'weeknight', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'pies-and-tarts', 'desserts', 'eggs-dairy', 'fruit', 'oven', 'pies', 'dietary', 'high-calcium', 'high-in-something', 'tropical-fruit', 'pineapple', 'equipment', '4-hours-or-less'", "984.5, 92.0, 108.0, 93.0, 63.0, 143.0, 27.0", "24", "'cheese tart shells:', 'combine the shortening and cheese spread in a medium bowl', 'cut flour into the cheese mixture with two knives until well blended', 'shape into a roll 1 1 / 4-inch in diameter and 12 inches long', 'wrap completely in waxed paper or plastic wrap', 'refrigerate for 1 hour or longer', 'preheat oven to 375 degrees f', 'remove the dough from the refrigerator and unwrap', 'slice 1 / 8-inch thick', 'using 12 muffin cups or 3-inch tart pans , place 1 slice of dough in the bottom of each', 'overlap 5 slices around the outside of each', 'gently press together', 'pierce the bottoms and sides with a fork', 'bake 18 to 20 minutes in the preheated oven until lightly browned', 'cool in the pans on a rack and gently remove the shells when cold to the touch', 'filling:', 'cut each orange section into 3 pieces and set aside', 'drain the pineapple , reserving the syrup', 'combine the pineapple syrup , orange juice , and lemon juice in a saucepan', 'stir in the rest of the ingredients except the cheddar cheese , and cook , stirring gently , over medium heat until the mixture thickens and bubbles', 'stir in the orange pieces and pineapple', 'refrigerate for at least 1 hour', 'spoon the chilled filling into the baked tart shells and sprinkle each tart with about 1 tbls of the cheddar cheese', 'refrigerate until serving time'", "'vegetable shortening', 'american cheese spread', 'unbleached flour', 'navel oranges', 'pineapple', 'sugar', 'cornstarch', 'salt', 'orange juice', 'lemon juice', 'cheddar c