INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("6742", "old fashioned apple cider pie", "this is an adopted recipe. i shall be trying it out and then post any additional information if needs be. feel free to try it out first and share your comments! ", "53", "'60-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'occasion', 'low-protein', 'healthy', 'pies-and-tarts', 'desserts', 'fruit', 'oven', 'pies', 'dietary', 'low-sodium', 'low-cholesterol', 'low-calorie', 'healthy-2', 'low-in-something', 'apples', 'equipment'", "257.3, 11.0, 140.0, 4.0, 2.0, 12.0, 16.0", "20", "'divide the pastry almost in half and roll out the larger half on a lightly floured surface to a 13-inch circle', 'line a 9-inch pie tin with the pastry', 'trim the edge to 1 / 2-inch beyond the rim of the pie tin', 'combine the apples , 1 cup of apple cider and the sugar in a 3-quart saucepan and cook , over high heat , until the mixture comes to a boil', 'reduce the heat to low and simmer for 8 minutes or until the apples are tender', 'drain the apples , reserving the syrup', 'add enough additional apple cider to the syrup to make 1 1 / 3 cups', 'return the syrup and apples to the saucepan', 'combine the cornstarch and water in a small bowl and stir until well blended', 'stir the cornstarch mixture and the cinnamon into the apple mixture', 'cook over medium heat , stirring constantly , until the mixture comes to a boil', 'remove from the heat and stir in the butter , then pour the mixture into the pastry lined pie tin', 'roll out the remaining pastry to an 11-inch circle', 'fold the pastry gently , so as not to tear it , into quarters and cut slits in the folds', 'gently unfold the pastry on to the top of the filling and trim the edge to 1-inch beyond the rim of the pie tin', 'fold the top crust under the lower crust and form a ridge by fluting the edge of the pie', 'bake in a 400 degree f