INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("6746", "rice with garlic and pine nuts", "this recipe combines the julia child method of cooking garlic in butter, and adds other ingredients for a lovely finished dish!  :)", "0", "'15-minutes-or-less', 'time-to-make', 'main-ingredient', 'preparation', 'occasion', '5-ingredients-or-less', 'fruit', 'rice', 'vegetables', 'easy', 'vegetarian', 'nuts', 'dietary', 'low-sodium', 'low-in-something', 'pasta-rice-and-grains'", "773.9, 31.0, 2.0, 0.0, 26.0, 29.0, 44.0", "15", "'remove all the cloves of garlic from the head', 'take a smallish saucepan and heat to boiling about a cup of water', 'add the cloves of garlic and simmer for about 1 minute- drain , peel the garlic and set aside', 'in a small saucepan , melt the butter together with the olive oil over medium heat', 'reduce the temperature low , then add the peeled garlic cloves to the butter mixture', 'stir well', 'saute slowly on lowest heat until tender and golden , at least 20 minutes , stirring and checking occasionally to make sure they do not burn', 'remove cloves from pan - pour any remaining butter into a wide skillet', 'thoroughly mash the garlic with a fork', 'heat the ""garlic drippings"" in the wide skillet', 'swirl in the mashed garlic', 'add the cooked rice and pine nuts', 'saute , stirring and tossing , until the rice is heated through and has absorbed the butter', 'season with salt and freshly ground pepper', 'serve hot'", "'unsalted butter', 'garlic head', 'rice', 'pine nuts', 'salt & pepper'", "5", "https://www.food.com/recipe/6746");
