INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("6947", "muffins basic and variations", "", "20", "'30-minutes-or-less', 'time-to-make', 'course', 'preparation', 'breads', 'oven', 'muffins', 'dietary', 'quick-breads', 'equipment'", "551.5, 48.0, 26.0, 37.0, 20.0, 27.0, 19.0", "30", "'grease 12 2 1 / 2-inch muffin cups', 'heat oven to 400 degrees f', 'sift flour , baking powder , sugar and salt into a medium-sized bowl', 'stir to mix well', 'in a small bowl , beat egg with a fork', 'add milk and oil', 'add all at once to dry ingredients', 'stir mixture only until dry ingredients are moistened', 'batter will be lumpy', 'drop batter from a tablespoon into prepared muffins pans , filling each cup half to two-thirds full', 'bake 15 to 20 minutes , or until golden brown', 'remove from pan and serve hot with butter , jam or marmalade', 'variations:', 'ginger muffins: add 1 / 2 cup finely diced candied ginger to flour mixture before adding liquid', 'banana pecan muffins: prepare muffin batter but use only 1 / 2 cup milk', 'add 1 / 2 cup chopped pecans and 1 / 4 t ground nutmeg to sifted flour', 'add 1 cup mashed , peeled banana with the egg , milk and oil', 'blueberry muffins: toss 1 cup washed and well-drained fresh or frozen blueberries with sifted flour mixture before adding liquid', 'orange muffins: cut 2 peeled navel oranges into sections', 'when batter is in the cups , place an orange section on top of each and sprinkle lightly with granulated sugar', 'cheese muffins: fold 1 / 2 cup grated sharp yellow cheese into muffin mix with the last few strokes on batter', 'serve hot with scrambled eggs and bacon for a special breakfast', 'surprise muffins: fill muffin cups 1 / 3rd full of batter', 'drop 1 / 2 t of your favorite jelly in center of batter', 'add batter to fill cup 2 / 3rds full', 'kids just love these as you will', 'coconut muffins: add 1 c shredded coconut with the last few strokes of mixing', 'for a snack have coconut muffins , butter and milk', 'chive muffins: fold 1 / 4 cup chives into the batter during the last few strokes and serve at dinner', 'great with a steak and salad'", "'unbleached all-purpose flour', 'baking powder', 'granulated sugar', 'salt', 'egg', 'milk', 'vegetable oil'", "7", "https://www.food.com/recipe/6947");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("6949", "butter pecan muffins", "there is nothing like freshly baked muffins in the morning for breakfast with a nice hot cup of coffee.  i hope you enjoy this recipe.", "30", "'30-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'cuisine', 'preparation', 'occasion', 'north-american', 'breads', 'breakfast', 'fruit', 'oven', 'holiday-event', 'muffins', 'nuts', 'seasonal', 'quick-breads', 'brunch', 'taste-mood', 'equipment'", "243.5, 21.0, 44.0, 12.0, 8.0, 20.0, 9.0", "11", "'preheat oven to 400 degrees', 'grease muffin pans', 'combine flour , pecans , and brown sugar in mixing bowl', 'set aside', 'in another bowl , beat egg', 'stir in milk , butter , and vanilla', 'add liquid ingredients all at once to flour mixture', 'stir only until flour is moistened', 'fill muffin cups 2 / 3 full', 'bake about 15 minutes or until golden brown', 'serve warm with butter or jam and enjoy !'", "'self-rising flour', 'pecans', 'brown sugar', 'egg', 'milk', 'butter', 'vanilla extract'", "7", "https://www.food.com/recipe/6949");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("6951", "pecan cinnamon muffins", "this is my adopted recipe feb 2005. i made this and did a few changes, i changed the 1/4 tsp cinnamon to 1 tsp and added 1 tsp vanilla. i like lots of nuts, i used 1 cup.", "35", "'60-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'breads', 'fruit', 'oven', 'muffins', 'nuts', 'dietary', 'quick-breads', 'equipment'", "433.0, 40.0, 71.0, 14.0, 11.0, 18.0, 14.0", "9", "'sift together flour , baking powder , salt and cinnamon into mixing bowl', 'add white and brown sugar', 'stir in pecans', 'combine egg , oil and milk and vanilla in small bowl', 'blend well', 'add all at once to dry ingredients , stirring just enough to moisten', 'batter will be thick', 'spoon batter into 12 greased 2 1 / 2-inch muffin-pan cups or 6- 3 1 / 2 muffin cups', 'bake in 375f oven about 20 minutes for 12 muffins or about 30 minutes for 6 -3 1 / 2 inch muffin cups or until tester comes out clean'", "'all-purpose flour', 'sugar', 'brown sugar', 'baking powder', 'salt', 'cinnamon', 'egg', 'vegetable oil', 'milk', 'vanilla extract', 'pecans'", "11", "https://www.food.com/recipe/6951");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("6952", "cranberry sour cream muffins", "this is my adopted recipe from feb 2005. i have changed a few ingredients from when coffeemom and karenann000 reviewed it. i made mine in 6- 3 1/2 inch muffin cups. i find the sour cream and cranberries go so well together.absolutely delicious. i don't find it needs any addition flavoring.", "35", "'60-minutes-or-less', 'time-to-make', 'course', 'preparation', 'breads', 'oven', 'muffins', 'dietary', 'quick-breads', 'equipment'", "419.9, 25.0, 104.0, 11.0, 13.0, 27.0, 20.0", "9", "'sift together flour , 1 / 4 cup sugar , baking soda and salt into bowl', 'combine egg , sour cream , milk and oil in small bowl', 'blend well', 'add all at once to dry ingredients , stirring just enough to moisten', 'combine cranberries and 1 / 2 cup sugar', 'stir into batter', 'spoon batter into greased 3 1 / 2-inch muffin-pan cups , filling equally , or use 12 cup 2 1 / 2 inch muffin pan', 'bake in 375f oven 20 minutes for 12 muffins and 30 minutes for 6 muffins or until golden brown', 'good hot or cold with butter , easy to reheat in micro'", "'all-purpose flour', 'sugar', 'baking soda', 'salt', 'egg', 'sour cream', 'milk', 'vegetable oil', 'raw cranberries'", "9", "https://www.food.com/recipe/6952");
