INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("6963", "molasses refrigerator muffins", "this is an orphan recipe that i adopted from recipezaar.  when i made them, i used buttermilk, but i'd like to try them again with the sour milk.  the only other thing was i didn't cook the first batch quite long enough and they sank in the middle.  was still good.", "35", "'60-minutes-or-less', 'time-to-make', 'course', 'preparation', 'healthy', 'breads', 'oven', 'refrigerator', 'muffins', 'dietary', 'quick-breads', 'equipment'", "418.2, 12.0, 162.0, 19.0, 14.0, 11.0, 26.0", "11", "'sift together flour , baking soda , salt , cinnamon , ginger , cloves , allspice and nutmeg', 'set aside', 'cream together shortening and sugar in mixing bowl until light anf fluffy , using electric mixer at medium speed', 'add eggs beat well', 'blend in molasses and butter / sour milk', 'add dry ingredients all at once , stirring just enough to moisten', 'stir in raisins', 'spoon into greased 3-inch muffin-pan cups , filling 1 / 2 full', 'bake in 350 degree oven 20 minutes or until golden brown', 'serve hot with butter and jam', 'batter can be stored in refrigerator in covered container for up to 3 weeks'", "'unbleached flour', 'baking soda', 'salt', 'cinnamon', 'ginger', 'clove', 'allspice', 'nutmeg', 'vegetable shortening', 'sugar', 'eggs', 'molasses', 'buttermilk', 'raisins'", "14", "https://www.food.com/recipe/6963");
