INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("6969", "pickled cocktail okra", "choose the smaller, more tender okra pods for pickling. if you don't like the heat, you may leave out the hot peppers.   ", "0", "'15-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'occasion', 'appetizers', 'canning', 'vegetables', 'dietary', 'technique'", "122.2, 1.0, 19.0, 349.0, 10.0, 0.0, 6.0", "8", "'wash okra and pack into clean jars with screw tops', 'into each jar put 1 red or green hot pepper , 1 clove of garlic , 1 / 2 teaspoon celery seed and 1 / 2 teaspoon mustard seed', 'in a saucepan , combine vinegar , water & pickling salt', 'bring to a boil , then pour over the okra , filling almost to the top but leaving a half inch ""headspace""', 'wipe rims of jars with clean cloth then place lids and rings on the jars and seal', 'process in a boiling water bath for 10 minutes then remove and allow to cool', ""if you are in an area which is above 1000 feet in elevation , you may need to process longer or you may check with the usda's guidelines for canning & processing at their website"", 'these pickled okra need to set up for 2 months'", "'fresh okra', 'hot red peppers', 'garlic', 'distilled white vinegar', 'water', 'pickling salt', 'celery seeds', 'mustard seeds'", "8", "https://www.food.com/recipe/6969");
