INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("7011", "sourdough pancakes  1", "if you don't have time to make traditional sourdough bread, this zaar recipe that i adopted is perfect for using up your starter.  i prepared the recipe and was pleasantly surprised by the taste of the pancakes.  the recipe makes a super thin batter that results in a light pancake with a subtle sourdough taste.  because the batter is so thin, the pancakes cook very quickly making this perfect for those rushed, hectic mornings.  my family and i really enjoyed these topped with my recipe for hot spiced fruit recipe #111649.  don't have any sourdough starter?  i highly recommend using  sourdough starter recipe #78816 by janice boice.  ", "0", "'15-minutes-or-less', 'time-to-make', 'course', 'preparation', 'pancakes-and-waffles', 'breakfast', 'dietary'", "129.1, 10.0, 0.0, 16.0, 8.0, 8.0, 4.0", "5", "'making sure the sourdough starter is at room temperature , mix all ingredients well', 'be careful not to over mix', 'small lumps are ok', 'lightly grease a hot cast iron griddle', 'drop onto griddle with a large spoon while the batter is still rising'", "'active starter', 'pancake mix', 'egg', 'cooking oil', 'milk', 'baking soda'", "6", "https://www.food.com/recipe/7011");
