INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("7076", "italian style vegetable soup", "there are as many versions of vegetable soup in italy as there there are cooks.  pancetta - pancetta is the same cut of pork as bacon.  it is cured with salt and is not smoked.  it comes rolled up like a large salami.  widely used in italian cooking, especially in emilia-romagna, it is vital to many dishes.  if available, buy a large quantity.  cut into several pieces and freeze it.  you can substitute domestic bacon for pancetta.  it must be blanched in boiling water for two three minutes to reduce the smoky flavour.  fresh side pork can also be used.", "75", "'time-to-make', 'course', 'main-ingredient', 'preparation', 'occasion', 'soups-stews', 'vegetables', 'dietary', '4-hours-or-less'", "207.9, 19.0, 23.0, 36.0, 20.0, 14.0, 4.0", "22", "'heat oil in a large saucepan', 'add parsley and garlic', 'saute over medium heat', 'before garlic changes colour , add pancetta', 'saute until lightly browned', 'stir in cabbage', 'cover and cook 1 to 2 minutes', 'add remaining vegetables to saucepan', 'cover and cook about 5 minutes', 'add broth and water , if using , and prosciutto rind or ham shank', 'cover and reduce heat', 'simmer 40 to 50 minutes', 'remove half the vegetables with a slotted spoon', 'place in a blender or food processor and process until smooth', 'return to saucepan', 'season with salt and pepper', 'serve hot with parmesan cheese sprinkled over top', 'variation:', 'toast about 20 thick slices of italian bread', 'place 2 slices in each soup bowl and sprinkle generously with parmesan cheese', 'ladle soup into bowls', 'serve with additional parmesan cheese'", "'chicken broth', 'olive oil', 'parsley', 'garlic', 'pancetta', 'cabbage', 'onion', 'carrots', 'celery', 'potato', 'zucchini', 'tomatoes', 'mushroom', 'string bean', 'prosciutto rind', 'salt', 'pepper', 'parmesan cheese'", "18", "http