INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("7195", "oven style scotch eggs", "this is another recipe i adopted from recipezaar on feb.14 2005.  i've changed the recipe to make it fabulous.  a delicious treat.  brings back great childhood memories.  enjoy!", "45", "'60-minutes-or-less', 'time-to-make', 'preparation', 'easy', 'dietary', 'low-carb', 'low-in-something'", "987.5, 99.0, 25.0, 44.0, 138.0, 100.0, 8.0", "10", "'shell hard-boiled eggs', 'in a small bowl combine sausage meat with sage , garlic powder , onion powder , cayenne pepper and black pepper', 'divide sausage into 8 equal portions , flatten each portion to about 1 / 8-inch thickness', 'roll each egg in flour and place on top of each sausage patty', 'use each sausage patty to wrap around each egg until it is completely encased , pressing edges together to seal', 'beat the egg in a shallow dish and place breadcrumbs on a flat plate', 'dip each sausage-coated eggs in beaten egg and roll in crumbs', 'place on baking tray lined with aluminum foil and bake in oven at 375f for 25 to 30 minutes until golden brown', 'for a crispy crust spray very lightly with pam baking spray before baking', 'when ready to serve , line platter with lettuce and arrange scotch eggs , cut in half , among the lettuce'", "'sausage meat', 'sage', 'onion powder', 'garlic powder', 'cayenne pepper', 'ground black pepper', 'hard-boiled eggs', 'flour', 'egg', 'fine breadcrumbs'", "10", "https://www.food.com/recipe/7195");
