INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("7201", "wild goose", "this is a recipe that i adopted in the recipezaar restaurant give-away of feb 2005. just want to warn that i have not yet made this recipe and therefore cannot yet vouch for it, although i chose it because it appeared to me that it has potential to be tasty and interesting. i plan on eventually cooking this and, if needed, will post any changes i think are called for. ", "0", "'weeknight', '15-minutes-or-less', 'time-to-make', 'main-ingredient', 'preparation', 'for-1-or-2', 'fruit', 'poultry', 'oven', 'dietary', 'low-carb', 'low-in-something', 'apples', 'meat', 'goose', 'equipment', 'number-of-servings'", "5459.8, 534.0, 227.0, 223.0, 825.0, 545.0, 35.0", "14", "'soak plucked and cleaned goose overnight in well salted water', 'rinse and dry , then stuff with 2 onions and 2 apples , both quartered', 'place breast side up in open roaster pan and cover with 2 strips thick bacon', 'brown in 475 degree oven until bacon is crisp', 'take all fat from pan and discard', 'remove bacon', 'add to the pan 1 onion , quartered , 1 carrot , quartered , 1 cut up stalk of celery , 1 bay leaf , 2 or 3 sprigs of parsley , 1 / 2 teaspoon thyme , 2 cans consomme with 2 cans water , and 1 cup of dry red wine', 'cover the pan and roast at 375 degrees for 2 to 2-1 / 2 hours , basting often', 'goose should be done so that meat will readily pull off carcass', 'remove goose from roaster and place on warm platter', 'remove and discard stuffing', 'strain the gravy and thicken with 1 tablespoon cornstarch mixed with 2 tablespoons cold water', 'correct seasoning if necessary', 'serve with wild rice'", "'goose', 'onions', 'apples', 'bacon', 'carrot', 'celery', 'bay leaf', 'parsley', 'thyme', 'consomme', 'red wine', 'cornstarch', 'salt'", "13", "https://www.food.com/recipe/7201");
