INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("7347", "african groundnut  peanut  stew", "a delicious savoury stew popular throughout west africa.  this dish is commonly served with boiled rice, pounded yam, or boiled yam or fufu.  enjoy!", "75", "'time-to-make', 'course', 'preparation', 'main-dish', 'stews', 'dietary', '4-hours-or-less'", "455.6, 43.0, 29.0, 33.0, 75.0, 31.0, 5.0", "9", "'boil chicken pieces for 30-40 minutes , set aside once cooked and reserve stock', 'saute onions in olive oil until soft', 'add tomato paste to onions and stir for 2 minutes', 'add stock and stir until tomato paste is well incorporated', 'add peanut butter and stir until well incorporated with tomato stock mixture', 'add salt , pepper , pepper flakes , onion powder and garlic powder , and 1 / 2 sachet of knorr bullion powder and let stew simmer until stew thickens', 'after 20 minutes , add the cooked chicken pieces and simmer further for another 15 to 20 minutes', 'taste , and adjust spices according to taste', 'serve with your choice of choice of rice , yams or fufu'", "'olive oil', 'onion', 'chicken legs-thighs', 'tomato paste', 'chicken stock', 'peanut butter', 'salt', 'pepper', 'hot red pepper flakes', 'garlic powder', 'onion powder', 'knorr chicken bouillon'", "12", "https://www.food.com/recipe/7347");
