INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("7359", "pan fried trout", "this adoptee is the way we make trout on our backpacking trips, we take corneal and butter packets and hope we catch dinner.  just as easy to make at home, its a taste of the outdoors.", "20", "'30-minutes-or-less', 'time-to-make', 'main-ingredient', 'preparation', 'occasion', '5-ingredients-or-less', 'seafood', 'easy', 'fish', 'dietary', 'freshwater-fish', 'trout'", "467.0, 37.0, 0.0, 8.0, 96.0, 40.0, 3.0", "7", "'rinse the trout under cold running water', 'pat dry', 'sprinkle with salt and pepper', 'melt the butter in a large heavy skillet on medium-high heat', 'coat the trout in corn meal and shake off the excess', 'when the butter is melted , place the coated trout , flesh side down , in the skillet', 'fry 4-5 minutes , then turn and fry 4-5 minutes or until the flesh is golden brown and flaky'", "'trout', 'cornmeal', 'butter', 'salt & pepper'", "4", "https://www.food.com/recipe/7359");
