INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("7402", "brown sugar fudge  penuche", "", "0", "'15-minutes-or-less', 'time-to-make', 'course', 'preparation', 'occasion', 'low-protein', 'fudge', 'desserts', 'candy', 'dietary', 'low-sodium', 'low-in-something'", "482.9, 30.0, 297.0, 3.0, 3.0, 62.0, 26.0", "40", "'prepare:', 'prewarm the thermometer in hot water', 'use a 2-quart saucepan', 'butter the upper sides of the saucepan', 'measure all ingredients except the vanilla and optionals , and dump into the saucepan', 'grease and if necessary , line a 5 x 10-inch pan', 'fill glass with ice cubes and water and the sink with 1 / 2 inch of cold water', 'dissolve the sugar , stirring constantly with a wooden spoon , over low heat until the butter melts , the gritty sounds cease , and the spoon glides smoothly over the bottom of the pan', 'increase the heat to medium and bring to a boil', 'boil , after washing down any crystals that may have formed , with a pastry brush dipped in hot water from the thermometer bath , using as little water as possible', 'introduce the prewarmed thermometer', 'reduce the heat while keeping the fudge at a boil', 'stir no more than necessary', 'test the fudge mixture in the ice-cold water when the mixture thickens and bubbles become noisy', 'ball , formed in ice water , should hold its shape until the heat from your had begins to flatten it and should be al dente ~- slightly chewy -- between 230 and 240', 'because of the molasses and brown sugar , it can ball at a lower temperature than some other fudges', 'shock by placing the saucepan in the cold water in the sink', 'seed by adding , without stirring , the vanilla', 'then allow to cool', 'stir when luke warm and ""skin"" forms on the top', 'return the thermometer to its hot water bath to soak clean', 'stir the fudge thoroughly but not vigorously by hand , with an electric mixer , or with a food processor', 'pause frequently to allow the fudge to react', 'watch for the fudge to thicken , lose its sheen , and become lighter in color or streaked with lighter shade , give off some heat , suddenly stiffen', 'if mixing by had , the fudge will ""snap"" with each stroke', 'by mixer , mixer waves will become very distinct , by food processor , fudge will flow sluggishly back to the center when the processor is stopped', 'add the optionals (1 / 2 cup chopped nuts (walnuts , pecans , or hazelnuts ) before the fudge totally candies', 'pour , score , and store when cool in an airtight container in the refrigerator or at room temperature', 'recipe is easily doubled and can be frozen', 'variations:', 'honey brown sugar fudge: in step 1 , eleminate the unsweetened chocolate and replace the molasses with 1 / 4 cup of honey', 'the honey causes the fudge to ball at a higher temperature', 'chocolate honey brown sugar fudge: in step 1 , replace the heavy cream with light cream or evaporated milk and replace the molasses with 1 / 4 cup of honey', 'orange brown sugar fudge: in step 1 , eleminate molasses , chocolate but add 1 tbl corn syrup', 'in step 6 , add 1 tbl grated orange zest , plus if you can get it , 1 tsp of pure orange extract', 'peanut butter brown sugar fudge: in step 1 , eliminate the molasses and chocolate , replace the heavy cream with 1 / 4 cup of milk and 1 / 4 cup of creamy peanut butter', 'to intensify the peanut butter flavor , add 1 / 3 of a cup of salted peanuts in step 9', ""praline brown sugar fudge: in step 1 , eliminating the molasses is optional -- you'ss get a more southern praline with it , a milder one without it--or compromise and use only 1 tbls"", 'eliminate the chocolate', 'in step 3 , when the mixture begins to thicken , add 1 1 / 2 cups of pecan halves slowly so as not to break the boil or cool the mixture too quickly'", "'white sugar', 'light brown sugar', 'heavy cream', 'molasses', 'unsweetened chocolate', 'butter', 'vanilla', 'nuts'", "8", "https://www.food.com/recipe/7402");
