INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("7443", "chocolate cinnamon cake roll", "okay, this isn't my recipe, although i do intend on making it soon.  it's one of the ones i acquired when the elves were clearing out the recipezaar account.  please tell me what it's like if you make it.  at some point i'll try to sort out the ingredients.  currently, things like the coffee liqueur appear three times because they're in the cake, filling and icing.", "0", "'15-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'desserts', 'oven', 'cakes', 'chocolate', 'dietary', 'equipment'", "251.2, 16.0, 97.0, 5.0, 7.0, 29.0, 11.0", "22", "'heat the oven to 375 degrees f', 'line a jelly roll pan , 15 1 / 2 x 10 1 / 2 x 1 inch with aluminum foil or waxed paper , grease generously', 'beat the eggs in a small mixer bowl , on high speed , until thick and lemon colored , about 5 minutes', 'pour into a larger mixer bowl', 'gradually beat in the sugar', 'beat in the water and 1 tsp of liqueur on low speed', 'gradually add the flour , 1 / 4 cup of cocoa , the baking powder and salt , beating just until the batter is smooth', 'pour into the jelly roll pan', 'bake until a wooden pick inserted in the center comes out clean , about 12 to 15 minutes', 'immediately loosen cake from the edges of the pan', 'invert on a towel sprinkled generously with cocoa', 'carefully remove the foil', 'trim off the stiff edges of cake if necessary', 'while hot , carefully roll the cake and towel up , together , from the narrow end', 'cool on a wire rack at least 30 minutes', 'unroll the cake', 'remove the towel', 'sprinkle 2 tb of the liqueur over the cake spread with cinnamon whipped cream', 'roll up', 'sprinkle with cocoa , if desired and refrigerate until serving time', 'cinnamon whipped cream:', 'beat all of the ingredients in a chilled bowl until stiff'", "'eggs', 'salt', 'sugar', 'cocoa', 'water', 'coffee-flavored liqueur', 'whipping cream', 'unbleached flour', 'powdered sugar', 'baking powder', 'ground cinnamon'", "11", "https://www.food.com/recipe/7443");
