INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("7447", "lima beans smitane", "this recipe came to me by way of the february 2005 recipe adoption.  i found the recipe to be very tasty and different way to prepare lima beans.  i did make some minor changes and updated the directions and the result is a creamy sauce with very nice flavor.  i hope you enjoy.", "20", "'30-minutes-or-less', 'time-to-make', 'main-ingredient', 'preparation', 'occasion', 'beans', 'vegetables', 'dietary', 'low-calorie', 'low-in-something'", "128.4, 6.0, 5.0, 6.0, 11.0, 12.0, 5.0", "5", "'add lima beans to a sauce pan with 1 cup of water and simmer 10-15 minutes until tender , then drain', 'meanwhile , mix together the sour cream , garlic powder , pimento , pepper and chives', 'over low heat return the drained lima beans to the sauce pan and mix in the sourcream mixture', 'gently stir until all ingredients are warmed through , being careful not to let the sour cream scorch', 'garnish with additional chopped chives if desired'", "'frozen lima beans', 'garlic powder', 'diced pimentos', 'salt', 'sour cream', 'fresh ground black pepper', 'fresh chives'", "7", "https://www.food.com/recipe/7447");
INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("7449", "buttered fried parsnips", "parsnips are one of the vegetables i think i ought to like; they are a locally grown vegetable, available all winter after all. but i never managed to be terribly enthusiastic about them until this recipe came along.", "55", "'60-minutes-or-less', 'time-to-make', 'main-ingredient', 'preparation', 'low-protein', '5-ingredients-or-less', 'vegetables', 'easy', 'dietary', 'low-sodium', 'low-calorie', 'low-in-something'", "273.1, 18.0, 43.0, 5.0, 5.0, 37.0, 13.0", "6", "'wash , trim and scrape the parsnips', 'cut into uniform pieces and boil in salted water , 25 to 30 minutes or until tender', 'drain well , and let dry', 'just before serving , heat the butter in a skillet and saute over moderate heat until light brown on all sides , letting the parsnips caramelize a little in their own sugar', 'it is best if you have a large enough pan that they can be cooked in a single layer', 'season with the nutmeg , salt and pepper and serve in a warm vegetable dish , garnished with parsley'", "'parsnips', 'salt & pepper', 'butter', 'parsley', 'nutmeg'", "5", "https://www.food.com/recipe/7449");
