INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("7478", "fresh peach crisp", "i read the suggestions and comments in the reviews when i tried this recipe that i adopted from the recipezaar account in february 2005.  instead of all the flour, sugar, and cinnamon going into the topping, i increased the amounts of each and mixed some with the peaches.  (originally the recipe called for 1 cup each flour and (white) sugar and only 1/2 teaspoons cinnamon.  i like cinnamon.)  the result was a dessert with a nice consistency, not runny, and with the sweetness distributed throughout, not just in the topping.  this fruity dessert is great served warm with vanilla ice cream.", "75", "'time-to-make', 'course', 'preparation', 'low-protein', 'healthy', 'cobblers-and-crisps', 'pies-and-tarts', 'desserts', 'oven', 'pies', 'dietary', 'low-sodium', 'low-in-something', 'equipment', '4-hours-or-less'", "520.6, 24.0, 265.0, 10.0, 9.0, 49.0, 31.0", "10", "'place sliced peaches in a mixing bowl', 'mix together 1 / 2 cup of the flour , 1 / 2 cup of the brown sugar , and 1 teaspoon of the cinnamon , then mix well with sliced peaches in bowl', 'place peach mixture in the bottom of a lightly buttered 9 x 13 inch baking dish', 'set aside', 'combine remaining flour , sugar , and cinnamon with the salt in a medium mixing bowl', 'cut in butter with a pastry blender until mixture resembles coarse meal', 'sprinkle mixture evenly over peaches in dish', 'bake in a preheated 375 degree oven for 45 minutes or until golden brown', 'spoon into individual serving bowls', 'serve warm with whipped cream or vanilla ice cream'", "'fresh peaches', 'cinnamon', 'all-purpose flour', 'salt', 'light brown sugar', 'butter'", "6", "https://www.food.com/recipe/7478");
