INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("7507", "creole stuffed eggplant  aubergine", "i love eggplant, and am always looking for delicious new ways of preparing them.  here's an interesting recipe for stuffing the purple beauties!  :)", "60", "'60-minutes-or-less', 'time-to-make', 'main-ingredient', 'cuisine', 'preparation', 'north-american', 'low-protein', 'vegetables', 'american', 'southern-united-states', 'oven', 'creole', 'dietary', 'low-sodium', 'low-calorie', 'low-carb', 'low-in-something', 'equipment'", "315.9, 30.0, 39.0, 16.0, 12.0, 57.0, 10.0", "15", "'preheat oven to 375 degrees f', 'heat the butter and olive oil together in large skillet', 'add the onion , celery , and garlic and saute over low heat until the onion is translucent', 'add the diced bell pepper and continue to saute until the onion is golden', 'in the meantime , stem the eggplants and cut them in half lengthwise', 'with a sharp knife , score each half several times lengthwise and across , carefully removing the pulp', 'leave a sturdy shell of about 1 / 4"" all around', 'chop the eggplant pulp and add it to the skillet mixture along with all the remaining ingredients except the bread crumbs', 'add a bit of water , just enough to keep the mixture moist', 'simmer , covered , over low heat until the eggplant is tender , stirring occasionally', 'stir in the bread crumbs', 'set the eggplant shells in an oiled shallow baking dish , which should be of a size that they can be securely propped up against each other', 'stuff the eggplant shells with the sauteed mixture , then drizzle them with 2 tablespoons of melted butter apiece', 'bake at 375 degrees f for 30-40 minutes , or until the shells are tender but not collapsed', 'serve with a bit of grated parmesan for garnish'", "'butter', 'olive oil', 'fresh basil', 'onion', 'fresh parsley', 'celery', 'dried thyme', 'garlic cloves', 'cayenne pepper', 'green bel