INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("7573", "minty mousse pie au chocolat", "this is a recipe i adopted from the recipezaar account.  update may 30, 2006: this is great!  the mint flavour somewhat dissipates but it is still lurking in there!  as it was hot in my kitchen, the chocolate mixture never got to room temperature.  therefore, mine never really ""set up"" properly.  so i froze it.  that worked beautifully!  it was like a fudgsicle in a pie shell.  it was somewhat difficult to cut but it wasn't impossible.  i look forward to making this again and again.  it was a great treat on an unbearably hot day!  plus, it is nice to know that i have a special sweet treat hiding in my freezer just waiting for me!  (chilling time is not included)", "65", "'weeknight', 'time-to-make', 'course', 'preparation', 'occasion', 'pies-and-tarts', 'desserts', 'refrigerator', 'candy', 'pies', 'dietary', 'equipment', '4-hours-or-less'", "565.8, 54.0, 172.0, 11.0, 11.0, 87.0, 20.0", "16", "'in medium heavy gauge saucepan , combine 4 t sugar , cornstarch , and mint-chocolate chips', 'gradually stir in milk', 'cook , stirring constantly , over medium heat until mixture boils', 'boil 1 minute', 'remove from heat', 'transfer to a large bowl', 'cover surface of chocolate mixture with plastic wrap', 'lay the plastic wrap on the surface as to prevent a skin from forming', 'cool to room temperature', 'in a medium bowl , combine heavy cream with remaining 2 t sugar', 'beat until stiff', 'remove plastic wrap from chocolate', 'beat well', 'fold in whipped cream', 'spoon into crust', 'chill until firm'", "'sugar', 'cornstarch', 'mint chocolate chips', 'milk', 'heavy cream', 'graham cracker crust'", "6", "https://www.food.com/recipe/7573");
