INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("7812", "easy dinner party marinated mushrooms", "i don't cook in my microwave very often, but here's a good recipe for making tasty mushrooms for the buffet table or dinner party.  sometimes shortcuts really are helpful!  :)", "45", "'60-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'occasion', 'appetizers', 'lunch', 'condiments-etc', 'vegetables', 'microwave', 'dinner-party', 'marinades-and-rubs', 'dietary', 'equipment'", "54.1, 0.0, 20.0, 6.0, 3.0, 0.0, 2.0", "12", "'combine wine , vinegar , sugar , garlic , green onion , red pepper and red pepper flakes in 8 cup microwaveable casserole', 'cover', 'microwave on high for 3 minutes or until mixture is simmering', 'add mushrooms', 'cover', 'microwave at high 6 minutes or until mushrooms are tender but not mushy', 'stir partway through cooking', 'stir in salt , oregano and pepper', 'let stand , covered , for 30 minutes', 'transfer to covered jar or container', 'refrigerate up to 1 week', 'lower powered machines might take a couple of minutes longer , higher will take about what is specified above'", "'white wine', 'white wine vinegar', 'balsamic vinegar', 'brown sugar', 'garlic cloves', 'green onion', 'red bell pepper', 'hot red pepper flakes', 'fresh mushrooms', 'salt', 'fresh ground black pepper', 'dried oregano leaves'", "12", "https://www.food.com/recipe/7812");
