INSERT INTO recipes (recipeID, name, description, minutes, tags, nutrition, n_steps, steps, ingredients, n_ingredients, url) VALUES ("7817", "chocolate mayonnaise sheet cake", "making cake with mayonnaise is an old tried and true method, but many folks are surprised when they first learn about it.  never fear, it really does work!  and deliciously so, i might add... :)", "0", "'15-minutes-or-less', 'time-to-make', 'course', 'main-ingredient', 'preparation', 'for-large-groups', 'desserts', 'oven', 'cakes', 'chocolate', 'dietary', 'equipment', 'number-of-servings'", "257.8, 6.0, 136.0, 13.0, 6.0, 11.0, 17.0", "16", "'preheat oven to 350 degrees f', 'in a bowl , sift together the flour , cocoa , baking soda , and salt', 'set aside', 'in the bowl of an electric mixer , combine the mayonnaise and sugar and beat at medium until blended', 'with mixer running , gradually add the cold water and vanilla', 'add dry ingredients to mayonnaise mixture , beating until well blended , about 2 minutes', 'pour batter into buttered 13x9x2-inch baking pan', 'bake in 350 degree f oven 40 minutes or until cake tests done', 'cool in cake in pan on rack', 'meanwhile , begin your fluffy chocolate frosting: in a saucepan over medium heat , combine the brown sugar , half and half or light cream , and butter', 'cook over medium heat , stirring constantly , until mixture comes to a boil', 'let boil 1 minute , then remove from heat', 'stir in the chocolate chips and vanilla', 'beat with an electric mixer at high speed until the consistency is fluffy and is spreadable', 'when cake has cooled , frost it with the fluffy chocolate frosting', 'cut in squares and enjoy ! :)'", "'flour', 'cocoa', 'baking soda', 'salt', 'mayonnaise', 'sugar', 'water', 'vanilla extract', 'brown sugar', 'chocolate chips', 'half-and-half', 'butter'", "12", "https://www.food.com/recipe/7817");
